'use client';

import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Eye, EyeOff, Lock, AlertCircle, Shield, Smartphone } from 'lucide-react';

const AdminLogin: React.FC = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [twoFactorCode, setTwoFactorCode] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState('');
  const [isLoggingIn, setIsLoggingIn] = useState(false);
  const [twoFactorRequired, setTwoFactorRequired] = useState(false);
  const [twoFactorMethod, setTwoFactorMethod] = useState<'totp' | 'email'>('totp');
  const [deviceFingerprint] = useState(() => {
    // Generate a simple device fingerprint
    return btoa(navigator.userAgent + Date.now().toString()).substring(0, 32);
  });

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoggingIn(true);
    setError('');

    try {
      const response = await fetch('/api/admin/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email,
          password,
          deviceFingerprint,
        }),
      });

      const data = await response.json();

      if (!response.ok) {
        setError(data.error || 'Login failed');
        setIsLoggingIn(false);
        return;
      }

      if (data.twoFactorRequired) {
        setTwoFactorRequired(true);
        setTwoFactorMethod(data.method);
        setIsLoggingIn(false);
        return;
      }

      // Login successful, redirect to dashboard
      window.location.href = '/admin/dashboard';
    } catch (err) {
      setError('An unexpected error occurred');
      console.error('Login error:', err);
      setIsLoggingIn(false);
    }
  };

  const handleTwoFactorSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoggingIn(true);
    setError('');

    try {
      const response = await fetch('/api/admin/verify-2fa', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email,
          twoFactorCode,
          deviceFingerprint,
        }),
      });

      const data = await response.json();

      if (!response.ok) {
        setError(data.error || '2FA verification failed');
        setIsLoggingIn(false);
        return;
      }

      // 2FA verification successful, redirect to dashboard
      window.location.href = '/admin/dashboard';
    } catch (err) {
      setError('An unexpected error occurred');
      console.error('2FA verification error:', err);
      setIsLoggingIn(false);
    }
  };

  const handleBackToLogin = () => {
    setTwoFactorRequired(false);
    setTwoFactorCode('');
    setError('');
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="w-full max-w-md mx-auto"
    >
        {/* Header */}
        <div className="text-center mb-6 sm:mb-8">
          <div className="w-16 h-16 sm:w-20 sm:h-20 bg-accent rounded-full flex items-center justify-center mx-auto mb-4">
            <Lock className="w-8 h-8 sm:w-10 sm:h-10 text-white" />
          </div>
          <h1 className="text-2xl sm:text-3xl font-bold text-gray-900 dark:text-white mb-2">
            Admin Access
          </h1>
          <p className="text-sm sm:text-base text-gray-600 dark:text-gray-400 px-2">
            Enter your credentials to access the admin panel
          </p>
        </div>

        {/* Login Form */}
        <motion.form
          onSubmit={handleLogin}
        className="bg-white dark:bg-gray-800 rounded-xl sm:rounded-2xl shadow-xl p-4 sm:p-6 lg:p-8"
          initial={{ scale: 0.9 }}
          animate={{ scale: 1 }}
          transition={{ delay: 0.2 }}
        >
          {/* Email Field */}
          <div className="mb-4 sm:mb-6">
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
              Email Address
            </label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="w-full px-3 sm:px-4 py-2 sm:py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-accent focus:border-transparent bg-white dark:bg-gray-700 text-gray-900 dark:text-white text-sm sm:text-base"
              placeholder="admin@limitlessinfotech.com"
              required
            />
          </div>

          {/* Password Field */}
          <div className="mb-4 sm:mb-6">
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
              Password
            </label>
            <div className="relative">
              <input
                type={showPassword ? 'text' : 'password'}
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w-full px-3 sm:px-4 py-2 sm:py-3 pr-10 sm:pr-12 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-accent focus:border-transparent bg-white dark:bg-gray-700 text-gray-900 dark:text-white text-sm sm:text-base"
                placeholder="Enter your password"
                required
              />
              <button
                type="button"
                onClick={() => setShowPassword(!showPassword)}
                className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600"
              >
                {showPassword ? <EyeOff className="w-4 h-4 sm:w-5 sm:h-5" /> : <Eye className="w-4 h-4 sm:w-5 sm:h-5" />}
              </button>
            </div>
          </div>

          {/* Error Message */}
          {error && (
            <motion.div
              initial={{ opacity: 0, y: -10 }}
              animate={{ opacity: 1, y: 0 }}
              className="flex items-center space-x-2 text-red-600 dark:text-red-400 text-sm mb-4 sm:mb-6 p-3 bg-red-50 dark:bg-red-900/20 rounded-lg"
            >
              <AlertCircle className="w-4 h-4 flex-shrink-0" />
              <span className="text-xs sm:text-sm">{error}</span>
            </motion.div>
          )}

          {/* Login Button */}
          <button
            type="submit"
            disabled={isLoggingIn}
            className="w-full bg-accent text-white py-2 sm:py-3 px-4 rounded-lg font-semibold hover:bg-accent-dark transition-colors disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center space-x-2 text-sm sm:text-base"
          >
            {isLoggingIn ? (
              <>
                <div className="w-4 h-4 sm:w-5 sm:h-5 border-2 border-white border-t-transparent rounded-full animate-spin" />
                <span>Signing In...</span>
              </>
            ) : (
              <>
                <Lock className="w-4 h-4 sm:w-5 sm:h-5" />
                <span>Access Admin Panel</span>
              </>
            )}
          </button>

          {/* Demo Credentials */}
          <div className="mt-4 sm:mt-6 p-3 sm:p-4 bg-gray-50 dark:bg-gray-700 rounded-lg">
            <p className="text-xs sm:text-sm text-gray-600 dark:text-gray-400 mb-2">
              <strong>Demo Credentials:</strong>
            </p>
            <p className="text-xs text-gray-500 dark:text-gray-500">
              Email: admin@limitlessinfotech.com<br />
              Password: Try@Admin123
            </p>
          </div>

          {/* Enterprise Link */}
          <div className="mt-4 text-center">
            <a
              href="/enterprise/login"
              className="text-sm text-accent hover:text-accent-dark dark:text-accent-light dark:hover:text-accent transition-colors"
            >
              Enterprise Suite Login →
            </a>
          </div>
        </motion.form>
    </motion.div>
  );
};

export default AdminLogin;

'use client';

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '../../../src/components/ui/Card';
import { Settings, Palette, Key, Mail, CreditCard, Zap, Globe, Shield, RefreshCw, CheckCircle, XCircle, AlertTriangle } from 'lucide-react';

interface SettingsData {
  branding: {
    logo: string;
    primaryColor: string;
    secondaryColor: string;
    favicon: string;
  };
  integrations: {
    stripe: { connected: boolean; mode: string };
    sendgrid: { connected: boolean; verified: boolean };
    googleAnalytics: { connected: boolean; trackingId?: string };
    googleWorkspace: { connected: boolean };
    slack: { connected: boolean };
    zoom: { connected: boolean };
  };
  email: {
    smtp: { host: string; port: string; secure: boolean; configured: boolean };
    templates: { count: number; active: boolean };
    dkim: { configured: boolean };
  };
  features: {
    aiFeatures: boolean;
    analytics: boolean;
    betaFeatures: boolean;
  };
  system: {
    environment: string;
    timezone: string;
    locale: string;
  };
}

const SettingsPage: React.FC = () => {
  const [settings, setSettings] = useState<SettingsData | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [lastUpdated, setLastUpdated] = useState<Date | null>(null);

  const fetchSettings = async () => {
    try {
      setLoading(true);
      const response = await fetch('/api/admin/settings');
      if (!response.ok) {
        throw new Error('Failed to fetch settings');
      }
      const data: SettingsData = await response.json();
      setSettings(data);
      setLastUpdated(new Date());
      setError(null);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Unknown error');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchSettings();
  }, []);

  const updateSetting = async (category: string, setting: string, value: any) => {
    try {
      const response = await fetch('/api/admin/settings', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ category, setting, value }),
      });

      if (!response.ok) {
        throw new Error('Failed to update setting');
      }

      // Refresh settings after update
      await fetchSettings();
    } catch (err) {
      console.error('Update failed:', err);
      alert('Failed to update setting');
    }
  };

  const getStatusIcon = (status: boolean) => {
    return status ? (
      <CheckCircle className="h-4 w-4 text-green-500" />
    ) : (
      <XCircle className="h-4 w-4 text-red-500" />
    );
  };

  const getStatusText = (status: boolean, connectedText: string = 'Connected', notConnectedText: string = 'Not Connected') => {
    return status ? connectedText : notConnectedText;
  };

  const getStatusColor = (status: boolean) => {
    return status ? 'text-green-600' : 'text-red-600';
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold tracking-tight">System Settings</h1>
          <p className="text-muted-foreground">Configure system-wide settings, integrations, and customization options.</p>
        </div>
        <div className="flex items-center space-x-2">
          {lastUpdated && (
            <span className="text-sm text-muted-foreground">
              Last updated: {lastUpdated.toLocaleTimeString()}
            </span>
          )}
          <button
            onClick={fetchSettings}
            disabled={loading}
            className="inline-flex items-center px-3 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 disabled:opacity-50"
          >
            <RefreshCw className={`w-4 h-4 mr-2 ${loading ? 'animate-spin' : ''}`} />
            Refresh
          </button>
        </div>
      </div>

      {error && (
        <Card className="border-red-200 bg-red-50">
          <CardContent className="p-4">
            <div className="flex items-center">
              <AlertTriangle className="h-5 w-5 text-red-500 mr-2" />
              <span className="text-red-700">Error loading settings: {error}</span>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Settings Categories */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        <Card className="cursor-pointer hover:shadow-md transition-shadow">
          <CardHeader>
            <CardTitle className="flex items-center text-lg">
              <Palette className="mr-2 h-5 w-5 text-blue-500" />
              Branding
            </CardTitle>
          </CardHeader>
          <CardContent>
            <p className="text-sm text-muted-foreground mb-4">
              Customize logos, themes, and visual identity across the platform.
            </p>
            <div className="space-y-2">
              <div className="flex justify-between text-sm">
                <span>Logo</span>
                <span className="text-green-600">✓ Configured</span>
              </div>
              <div className="flex justify-between text-sm">
                <span>Primary Color</span>
                <span className="text-green-600">✓ {settings?.branding.primaryColor || '#007bff'}</span>
              </div>
              <div className="flex justify-between text-sm">
                <span>Favicon</span>
                <span className="text-green-600">✓ Configured</span>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="cursor-pointer hover:shadow-md transition-shadow">
          <CardHeader>
            <CardTitle className="flex items-center text-lg">
              <Key className="mr-2 h-5 w-5 text-green-500" />
              API Keys
            </CardTitle>
          </CardHeader>
          <CardContent>
            <p className="text-sm text-muted-foreground mb-4">
              Manage third-party API integrations and authentication keys.
            </p>
            <div className="space-y-2">
              <div className="flex justify-between text-sm">
                <span>Stripe</span>
                <span className={`flex items-center ${getStatusColor(settings?.integrations.stripe.connected || false)}`}>
                  {getStatusIcon(settings?.integrations.stripe.connected || false)}
                  <span className="ml-1">{getStatusText(settings?.integrations.stripe.connected || false, settings?.integrations.stripe.mode === 'live' ? 'Live Mode' : 'Test Mode')}</span>
                </span>
              </div>
              <div className="flex justify-between text-sm">
                <span>SendGrid</span>
                <span className={`flex items-center ${getStatusColor(settings?.integrations.sendgrid.connected || false)}`}>
                  {getStatusIcon(settings?.integrations.sendgrid.connected || false)}
                  <span className="ml-1">{getStatusText(settings?.integrations.sendgrid.connected || false)}</span>
                </span>
              </div>
              <div className="flex justify-between text-sm">
                <span>Google Analytics</span>
                <span className={`flex items-center ${getStatusColor(settings?.integrations.googleAnalytics.connected || false)}`}>
                  {getStatusIcon(settings?.integrations.googleAnalytics.connected || false)}
                  <span className="ml-1">{getStatusText(settings?.integrations.googleAnalytics.connected || false)}</span>
                </span>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="cursor-pointer hover:shadow-md transition-shadow">
          <CardHeader>
            <CardTitle className="flex items-center text-lg">
              <Mail className="mr-2 h-5 w-5 text-purple-500" />
              Email & SMTP
            </CardTitle>
          </CardHeader>
          <CardContent>
            <p className="text-sm text-muted-foreground mb-4">
              Configure email delivery, SMTP settings, and notification templates.
            </p>
            <div className="space-y-2">
              <div className="flex justify-between text-sm">
                <span>SMTP Server</span>
                <span className={`flex items-center ${getStatusColor(settings?.email.smtp.configured || false)}`}>
                  {getStatusIcon(settings?.email.smtp.configured || false)}
                  <span className="ml-1">{getStatusText(settings?.email.smtp.configured || false, 'Configured', 'Not Configured')}</span>
                </span>
              </div>
              <div className="flex justify-between text-sm">
                <span>Email Templates</span>
                <span className="text-green-600">✓ {settings?.email.templates.count || 0} Active</span>
              </div>
              <div className="flex justify-between text-sm">
                <span>DKIM/SPF</span>
                <span className={`flex items-center ${getStatusColor(settings?.email.dkim.configured || false)}`}>
                  {getStatusIcon(settings?.email.dkim.configured || false)}
                  <span className="ml-1">{getStatusText(settings?.email.dkim.configured || false, 'Configured', 'Needs Setup')}</span>
                </span>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="cursor-pointer hover:shadow-md transition-shadow">
          <CardHeader>
            <CardTitle className="flex items-center text-lg">
              <CreditCard className="mr-2 h-5 w-5 text-yellow-500" />
              Payment Gateway
            </CardTitle>
          </CardHeader>
          <CardContent>
            <p className="text-sm text-muted-foreground mb-4">
              Set up payment processing, subscription plans, and billing rules.
            </p>
            <div className="space-y-2">
              <div className="flex justify-between text-sm">
                <span>Stripe Integration</span>
                <span className={`flex items-center ${getStatusColor(settings?.integrations.stripe.connected || false)}`}>
                  {getStatusIcon(settings?.integrations.stripe.connected || false)}
                  <span className="ml-1">{getStatusText(settings?.integrations.stripe.connected || false, settings?.integrations.stripe.mode === 'live' ? 'Live Mode' : 'Test Mode', 'Not Connected')}</span>
                </span>
              </div>
              <div className="flex justify-between text-sm">
                <span>Subscription Plans</span>
                <span className="text-green-600">✓ 3 Active</span>
              </div>
              <div className="flex justify-between text-sm">
                <span>Tax Settings</span>
                <span className="text-yellow-600">⚠ Incomplete</span>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="cursor-pointer hover:shadow-md transition-shadow">
          <CardHeader>
            <CardTitle className="flex items-center text-lg">
              <Globe className="mr-2 h-5 w-5 text-indigo-500" />
              Integrations
            </CardTitle>
          </CardHeader>
          <CardContent>
            <p className="text-sm text-muted-foreground mb-4">
              Connect with external services and third-party applications.
            </p>
            <div className="space-y-2">
              <div className="flex justify-between text-sm">
                <span>Google Workspace</span>
                <span className={`flex items-center ${getStatusColor(settings?.integrations.googleWorkspace.connected || false)}`}>
                  {getStatusIcon(settings?.integrations.googleWorkspace.connected || false)}
                  <span className="ml-1">{getStatusText(settings?.integrations.googleWorkspace.connected || false)}</span>
                </span>
              </div>
              <div className="flex justify-between text-sm">
                <span>Slack</span>
                <span className={`flex items-center ${getStatusColor(settings?.integrations.slack.connected || false)}`}>
                  {getStatusIcon(settings?.integrations.slack.connected || false)}
                  <span className="ml-1">{getStatusText(settings?.integrations.slack.connected || false)}</span>
                </span>
              </div>
              <div className="flex justify-between text-sm">
                <span>Zoom</span>
                <span className={`flex items-center ${getStatusColor(settings?.integrations.zoom.connected || false)}`}>
                  {getStatusIcon(settings?.integrations.zoom.connected || false)}
                  <span className="ml-1">{getStatusText(settings?.integrations.zoom.connected || false)}</span>
                </span>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="cursor-pointer hover:shadow-md transition-shadow">
          <CardHeader>
            <CardTitle className="flex items-center text-lg">
              <Zap className="mr-2 h-5 w-5 text-orange-500" />
              Feature Toggles
            </CardTitle>
          </CardHeader>
          <CardContent>
            <p className="text-sm text-muted-foreground mb-4">
              Enable or disable system features and experimental functionality.
            </p>
            <div className="space-y-2">
              <div className="flex justify-between text-sm">
                <span>AI Features</span>
                <button
                  onClick={() => updateSetting('features', 'aiFeatures', !settings?.features.aiFeatures)}
                  className={`flex items-center ${getStatusColor(settings?.features.aiFeatures || false)} hover:opacity-75`}
                >
                  {getStatusIcon(settings?.features.aiFeatures || false)}
                  <span className="ml-1">{getStatusText(settings?.features.aiFeatures || false, 'Enabled', 'Disabled')}</span>
                </button>
              </div>
              <div className="flex justify-between text-sm">
                <span>Analytics</span>
                <button
                  onClick={() => updateSetting('features', 'analytics', !settings?.features.analytics)}
                  className={`flex items-center ${getStatusColor(settings?.features.analytics || false)} hover:opacity-75`}
                >
                  {getStatusIcon(settings?.features.analytics || false)}
                  <span className="ml-1">{getStatusText(settings?.features.analytics || false, 'Enabled', 'Disabled')}</span>
                </button>
              </div>
              <div className="flex justify-between text-sm">
                <span>Beta Features</span>
                <button
                  onClick={() => updateSetting('features', 'betaFeatures', !settings?.features.betaFeatures)}
                  className={`flex items-center ${getStatusColor(settings?.features.betaFeatures || false)} hover:opacity-75`}
                >
                  {getStatusIcon(settings?.features.betaFeatures || false)}
                  <span className="ml-1">{getStatusText(settings?.features.betaFeatures || false, 'Enabled', 'Disabled')}</span>
                </button>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* System Configuration */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <Shield className="mr-2 h-5 w-5" />
            System Configuration
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div>
              <h4 className="font-medium mb-2">Environment</h4>
              <p className="text-sm text-muted-foreground">{settings?.system.environment || 'development'}</p>
            </div>
            <div>
              <h4 className="font-medium mb-2">Timezone</h4>
              <p className="text-sm text-muted-foreground">{settings?.system.timezone || 'UTC'}</p>
            </div>
            <div>
              <h4 className="font-medium mb-2">Locale</h4>
              <p className="text-sm text-muted-foreground">{settings?.system.locale || 'en-US'}</p>
            </div>
          </div>
          <div className="mt-6 pt-6 border-t">
            <div className="text-center">
              <Settings className="mx-auto h-12 w-12 text-muted-foreground mb-4" />
              <h3 className="text-lg font-semibold mb-2">Advanced Configuration</h3>
              <p className="text-muted-foreground mb-4">
                Additional system configuration options available for Super Admin users.
              </p>
              <p className="text-sm text-muted-foreground">
                Features include: environment variables, database settings, caching configuration, security policies, and performance tuning.
              </p>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
};

export default SettingsPage;

import { GET } from '../pages/route';
import { createServerClient } from '@supabase/ssr';
import { NextRequest } from 'next/server';

// Mock NextResponse
jest.mock('next/server', () => ({
  NextRequest: (global as any).NextRequest,
  NextResponse: (global as any).NextResponse,
}));

// Mock the createServerClient and cookies
jest.mock('@supabase/ssr', () => ({
  createServerClient: jest.fn(),
}));

jest.mock('next/headers', () => ({
  cookies: jest.fn(() => Promise.resolve({
    get: jest.fn(),
  })),
}));

describe('/api/pages GET', () => {
  beforeEach(() => {
    jest.clearAllMocks();
  });

  it('should return pages successfully', async () => {
    const mockPages = [
      { id: 1, page_name: 'Home', is_published: true },
      { id: 2, page_name: 'About', is_published: true },
    ];

    const mockSupabase = {
      auth: {
        getSession: jest.fn(() => Promise.resolve({ data: { session: { user: { id: 'admin-id' } } } })),
      },
      from: jest.fn()
        .mockImplementation((table) => {
          if (table === 'profiles') {
            return {
              select: jest.fn().mockReturnThis(),
              eq: jest.fn().mockReturnThis(),
              single: jest.fn(() => Promise.resolve({ data: { role: 'admin' }, error: null })),
            };
          } else if (table === 'pages') {
            return {
              select: jest.fn().mockReturnThis(),
              order: jest.fn(() => Promise.resolve({ data: mockPages, error: null })),
            };
          }
          return {};
        }),
    };

    (createServerClient as jest.Mock).mockReturnValue(mockSupabase);

    const mockRequest = new NextRequest('http://localhost:3000/api/pages');
    const response = await GET(mockRequest);
    const result = await response!.json();

    expect(response.status).toBe(200);
    expect(result.pages).toEqual(mockPages);
  });

  it('should handle database errors', async () => {
    const mockError = { message: 'Database connection failed' };

    const mockSupabase = {
      auth: {
        getSession: jest.fn(() => Promise.resolve({ data: { session: { user: { id: 'admin-id' } } } })),
      },
      from: jest.fn()
        .mockImplementation((table) => {
          if (table === 'profiles') {
            return {
              select: jest.fn().mockReturnThis(),
              eq: jest.fn().mockReturnThis(),
              single: jest.fn(() => Promise.resolve({ data: { role: 'admin' }, error: null })),
            };
          } else if (table === 'pages') {
            return {
              select: jest.fn().mockReturnThis(),
              order: jest.fn(() => Promise.resolve({ data: null, error: mockError })),
            };
          }
          return {};
        }),
    };

    (createServerClient as jest.Mock).mockReturnValue(mockSupabase);

    const mockRequest = new NextRequest('http://localhost:3000/api/pages');
    const response = await GET(mockRequest);
    const result = await response!.json();

    expect(response.status).toBe(500);
    expect(result.error).toBe('Failed to fetch pages');
  });
});

import { NextRequest, NextResponse } from 'next/server';
import { createClient } from '@supabase/supabase-js';

const supabase = createClient(
  process.env.NEXT_PUBLIC_SUPABASE_URL!,
  process.env.SUPABASE_SERVICE_ROLE_KEY!
);

export async function GET(request: NextRequest) {
  try {
    // Get analytics summary
    const { data: analyticsSummary, error: analyticsError } = await supabase
      .from('analytics_summary')
      .select('*')
      .single();

    if (analyticsError) {
      console.warn('Analytics summary not found, using defaults:', analyticsError);
    }

    // Get user count by role
    const { data: userStats, error: userError } = await supabase
      .from('profiles')
      .select('role')
      .not('role', 'is', null);

    if (userError) {
      console.error('Error fetching user stats:', userError);
    }

    // Get project stats
    const { data: projects, error: projectsError } = await supabase
      .from('projects')
      .select('status, created_at')
      .gte('created_at', new Date(Date.now() - 30 * 24 * 60 * 60 * 1000).toISOString());

    if (projectsError) {
      console.warn('Error fetching projects:', projectsError);
    }

    // Get leads count
    const { count: leadsCount, error: leadsError } = await supabase
      .from('leads')
      .select('*', { count: 'exact', head: true })
      .gte('created_at', new Date(Date.now() - 90 * 24 * 60 * 60 * 1000).toISOString());

    if (leadsError) {
      console.warn('Error fetching leads:', leadsError);
    }

    // Get testimonials count
    const { count: testimonialsCount, error: testimonialsError } = await supabase
      .from('testimonials')
      .select('*', { count: 'exact', head: true });

    if (testimonialsError) {
      console.warn('Error fetching testimonials:', testimonialsError);
    }

    // Calculate department distribution (mock data for now - would need proper department field)
    const departmentData = {
      Development: 64,
      Design: 28,
      Marketing: 21,
      Operations: 17,
      Management: 12
    };

    // Calculate project status distribution
    const projectStatus = projects?.reduce((acc, project) => {
      const status = project.status || 'planning';
      acc[status] = (acc[status] || 0) + 1;
      return acc;
    }, {} as Record<string, number>) || {};

    // Mock revenue data - would need actual revenue tracking
    const revenue = 847000; // $847K
    const lastMonthRevenue = 780000; // $780K
    const revenueGrowth = ((revenue - lastMonthRevenue) / lastMonthRevenue) * 100;

    // Mock client data
    const activeClients = 47;
    const newClientsThisQuarter = 12;
    const averageRating = 4.8;

    // Mock system uptime
    const systemUptime = 99.9;

    // Prepare response data
    const organizationData = {
      keyMetrics: {
        totalEmployees: userStats?.length || 142,
        activeProjects: projects?.length || 28,
        revenue: revenue,
        revenueGrowth: revenueGrowth,
        systemUptime: systemUptime,
        systemUptimeChange: 0.1
      },
      departmentDistribution: departmentData,
      projectStatus: {
        completed: projectStatus.completed || 18,
        inProgress: projectStatus.in_progress || 7,
        planning: projectStatus.planning || 2,
        onHold: projectStatus.on_hold || 1
      },
      clientPortfolio: {
        activeClients: activeClients,
        newThisQuarter: newClientsThisQuarter,
        averageRating: averageRating
      },
      recentActivity: [
        {
          type: 'project_completion',
          title: 'Project Alpha Completed',
          description: 'Delivered ahead of schedule with 98% client satisfaction',
          timestamp: new Date(Date.now() - 2 * 24 * 60 * 60 * 1000).toISOString()
        },
        {
          type: 'new_employee',
          title: 'New Team Member',
          description: 'Sarah Johnson joined the Design team',
          timestamp: new Date(Date.now() - 7 * 24 * 60 * 60 * 1000).toISOString()
        },
        {
          type: 'revenue_milestone',
          title: 'Revenue Milestone',
          description: 'Monthly recurring revenue exceeded $100K',
          timestamp: new Date(Date.now() - 14 * 24 * 60 * 60 * 1000).toISOString()
        }
      ],
      analytics: {
        siteVisits: analyticsSummary?.site_visits || 0,
        clientsCount: analyticsSummary?.clients_count || 0,
        leadsCount: leadsCount || 0,
        testimonialsCount: testimonialsCount || 0
      }
    };

    return NextResponse.json({
      success: true,
      data: organizationData
    });

  } catch (error) {
    console.error('Organization data fetch error:', error);
    return NextResponse.json(
      { error: 'Failed to fetch organization data' },
      { status: 500 }
    );
  }
}

'use client';
import { useQuery, useQueryClient } from '@tanstack/react-query';
import { createClientComponentClient } from '@supabase/auth-helpers-nextjs';
import { Suspense } from 'react';
import Calendar from '../../../src/components/enterprise/Calendar';
import Skeleton from '@/components/ui/Skeleton';
import { Plus } from 'lucide-react';

interface CalendarEvent {
  id: string;
  title: string;
  start: string;
  end: string;
  description?: string;
  attendees?: string[];
  color?: string;
  location?: string;
  allDay: boolean;
}

import { TypedSupabaseClient } from '../../../src/types';

async function fetchCalendarEvents(supabase: TypedSupabaseClient): Promise<CalendarEvent[]> {
  // Fetch events from Supabase (assume 'events' table with fields: title, start, end, description, attendees (json), color, location, all_day)
  const { data, error } = await supabase
    .from('events')
    .select('*')
    .gte('start', new Date().toISOString())
    .order('start', { ascending: true });

  if (error) throw error;

  if (!data || data.length === 0) {
    throw new Error('No calendar events found');
  }

  // Map data to CalendarEvent
  return data.map((item) => ({
    id: item.id,
    title: item.title,
    start: item.start,
    end: item.end,
    description: item.description,
    attendees: item.attendees || [],
    color: item.color || '#3b82f6',
    location: item.location,
    allDay: item.all_day || false,
  }));
}

const CalendarContent = () => {
  const supabase = createClientComponentClient();
  const queryClient = useQueryClient();

  const { data: events, isLoading, error } = useQuery({
    queryKey: ['calendar-events'],
    queryFn: () => fetchCalendarEvents(supabase),
  });

  if (error) {
    return (
      <div className="text-center py-8">
        <p className="text-red-500">Error loading calendar: {error.message}</p>
      </div>
    );
  }

  const handleEventClick = (event: any) => {
    // Handle event click, e.g., open modal or navigate to event details
    console.log('Clicked event:', event.id);
    alert(`Opening event ${event.id}`);
  };

  const handleEventAdd = async (date: Date) => {
    // Add new event to Supabase
    const newEvent = {
      title: 'New Event',
      start: date.toISOString(),
      end: new Date(date.getTime() + 60 * 60 * 1000).toISOString(), // 1 hour later
      description: '',
      attendees: [],
      color: '#3b82f6',
      location: '',
      allDay: false,
    };

    const { error } = await supabase
      .from('events')
      .insert({
        title: newEvent.title,
        start: newEvent.start,
        end: newEvent.end,
        description: newEvent.description,
        attendees: newEvent.attendees,
        color: newEvent.color,
        location: newEvent.location,
        all_day: newEvent.allDay,
      });

    if (error) {
      console.error('Error adding event:', error);
      alert('Failed to add event');
    } else {
      // Invalidate query to refetch
      queryClient.invalidateQueries({ queryKey: ['calendar-events'] });
      alert('Event added successfully!');
    }
  };

  const handleDateChange = (date: Date) => {
    // Handle date change, e.g., filter events or navigate
    console.log('Date changed to:', date);
  };

  if (isLoading) {
    return <CalendarLoading />;
  }

  return (
    <div className="space-y-8">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white">Company Calendar</h1>
          <p className="text-gray-600 dark:text-gray-400 mt-1">Schedule and manage team events</p>
        </div>
        <div className="flex items-center space-x-2">
          <button className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors flex items-center">
            <Plus className="w-4 h-4 mr-2" />
            New Event
          </button>
        </div>
      </div>

      <Suspense fallback={<CalendarLoading />}>
        <Calendar
          events={(events || []).map(event => ({
            id: event.id,
            title: event.title,
            date: new Date(event.start),
            startTime: event.allDay ? undefined : new Date(event.start).toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' }),
            endTime: event.allDay ? undefined : new Date(event.end).toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' }),
            location: event.location,
            description: event.description,
            color: event.color,
            allDay: event.allDay,
          }))}
          onEventClick={handleEventClick}
          onEventAdd={handleEventAdd}
          onDateChange={handleDateChange}
          title="Team Calendar"
        />
      </Suspense>
    </div>
  );
};

const CalendarLoading = () => (
  <div className="space-y-4">
    <Skeleton className="h-8 w-64" />
    <div className="grid grid-cols-7 gap-2">
      {Array.from({ length: 42 }).map((_, i) => (
        <Skeleton key={i} className="h-32 w-full" />
      ))}
    </div>
  </div>
);

export default function CalendarPage() {
  return (
    <Suspense fallback={<div className="text-center py-8">Loading calendar...</div>}>
      <CalendarContent />
    </Suspense>
  );
}

'use client';

import React, { useState, useEffect } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { createClient } from '@supabase/supabase-js';
import EnterpriseLayoutEnhanced from '@/components/enterprise/EnterpriseLayoutEnhanced';
import EnterpriseDataGrid from '@/components/enterprise/EnterpriseDataGrid';
import Button from '@/components/ui/Button';
import { Input } from '@/components/ui/Input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/Select';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/Dialog';
import Card, { CardHeader, CardContent, CardTitle } from '@/components/ui/Card';
import { Badge } from '@/components/ui/Badge';
import { Plus, Search, Edit, Trash2, Calendar, Users, DollarSign, Eye } from 'lucide-react';

const supabase = createClient(
  process.env.NEXT_PUBLIC_SUPABASE_URL!,
  process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!
);

interface Project {
  id: string;
  name: string;
  description: string;
  status: 'planning' | 'active' | 'on_hold' | 'completed' | 'cancelled';
  priority: 'low' | 'medium' | 'high' | 'critical';
  start_date: string;
  end_date: string;
  budget: number;
  client_id: string;
  manager_id: string;
  progress: number;
  created_at: string;
  updated_at: string;
  client?: { name: string };
  manager?: { name: string };
}

export default function ProjectsPage() {
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState<string>('all');
  const [priorityFilter, setPriorityFilter] = useState<string>('all');
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);
  const [editingProject, setEditingProject] = useState<Project | null>(null);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    status: 'planning',
    priority: 'medium',
    start_date: '',
    end_date: '',
    budget: 0,
    client_id: '',
    manager_id: '',
    progress: 0
  });

  const queryClient = useQueryClient();

  // Fetch projects with related data
  const { data: projects, isLoading } = useQuery({
    queryKey: ['projects'],
    queryFn: async () => {
      const { data, error } = await supabase
        .from('projects')
        .select(`
          *,
          client:clients(name),
          manager:team_members(name)
        `)
        .order('created_at', { ascending: false });

      if (error) throw error;
      return data as Project[];
    }
  });

  // Fetch clients for dropdown
  const { data: clients } = useQuery({
    queryKey: ['clients'],
    queryFn: async () => {
      const { data, error } = await supabase
        .from('clients')
        .select('id, name')
        .eq('status', 'active');

      if (error) throw error;
      return data;
    }
  });

  // Fetch team members for dropdown
  const { data: teamMembers } = useQuery({
    queryKey: ['team_members'],
    queryFn: async () => {
      const { data, error } = await supabase
        .from('team_members')
        .select('id, name')
        .eq('status', 'active');

      if (error) throw error;
      return data;
    }
  });

  // Create project mutation
  const createProjectMutation = useMutation({
    mutationFn: async (projectData: typeof formData) => {
      const { data, error } = await supabase
        .from('projects')
        .insert([projectData])
        .select()
        .single();

      if (error) throw error;
      return data;
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['projects'] });
      setIsCreateDialogOpen(false);
      resetForm();
      alert('Project created successfully');
    },
    onError: (error) => {
      alert('Failed to create project: ' + error.message);
    }
  });

  // Update project mutation
  const updateProjectMutation = useMutation({
    mutationFn: async ({ id, ...projectData }: typeof formData & { id: string }) => {
      const { data, error } = await supabase
        .from('projects')
        .update(projectData)
        .eq('id', id)
        .select()
        .single();

      if (error) throw error;
      return data;
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['projects'] });
      setEditingProject(null);
      resetForm();
      alert('Project updated successfully');
    },
    onError: (error) => {
      alert('Failed to update project: ' + error.message);
    }
  });

  // Delete project mutation
  const deleteProjectMutation = useMutation({
    mutationFn: async (id: string) => {
      const { error } = await supabase
        .from('projects')
        .delete()
        .eq('id', id);

      if (error) throw error;
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['projects'] });
      alert('Project deleted successfully');
    },
    onError: (error) => {
      alert('Failed to delete project: ' + error.message);
    }
  });

  const resetForm = () => {
    setFormData({
      name: '',
      description: '',
      status: 'planning',
      priority: 'medium',
      start_date: '',
      end_date: '',
      budget: 0,
      client_id: '',
      manager_id: '',
      progress: 0
    });
  };

  const handleCreate = () => {
    createProjectMutation.mutate(formData);
  };

  const handleUpdate = () => {
    if (editingProject) {
      updateProjectMutation.mutate({ ...formData, id: editingProject.id });
    }
  };

  const handleEdit = (project: Project) => {
    setEditingProject(project);
    setFormData({
      name: project.name,
      description: project.description,
      status: project.status,
      priority: project.priority,
      start_date: project.start_date,
      end_date: project.end_date,
      budget: project.budget,
      client_id: project.client_id,
      manager_id: project.manager_id,
      progress: project.progress
    });
  };

  const handleDelete = (id: string) => {
    if (confirm('Are you sure you want to delete this project?')) {
      deleteProjectMutation.mutate(id);
    }
  };

  // Filter projects based on search and filters
  const filteredProjects = projects?.filter(project => {
    const matchesSearch = project.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         project.description.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = statusFilter === 'all' || project.status === statusFilter;
    const matchesPriority = priorityFilter === 'all' || project.priority === priorityFilter;

    return matchesSearch && matchesStatus && matchesPriority;
  }) || [];

  // Calculate summary stats
  const stats = {
    total: projects?.length || 0,
    active: projects?.filter(p => p.status === 'active').length || 0,
    completed: projects?.filter(p => p.status === 'completed').length || 0,
    totalBudget: projects?.reduce((sum, p) => sum + (p.budget || 0), 0) || 0
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active': return 'bg-green-100 text-green-800';
      case 'planning': return 'bg-blue-100 text-blue-800';
      case 'on_hold': return 'bg-yellow-100 text-yellow-800';
      case 'completed': return 'bg-purple-100 text-purple-800';
      case 'cancelled': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'critical': return 'bg-red-100 text-red-800';
      case 'high': return 'bg-orange-100 text-orange-800';
      case 'medium': return 'bg-yellow-100 text-yellow-800';
      case 'low': return 'bg-green-100 text-green-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const columns = [
    { key: 'name', header: 'Project Name', sortable: true },
    { key: 'client', header: 'Client', render: (project: Project) => project.client?.name || 'N/A' },
    { key: 'manager', header: 'Manager', render: (project: Project) => project.manager?.name || 'N/A' },
    { key: 'status', header: 'Status', render: (project: Project) => (
      <Badge className={getStatusColor(project.status)}>
        {project.status.replace('_', ' ').toUpperCase()}
      </Badge>
    )},
    { key: 'priority', header: 'Priority', render: (project: Project) => (
      <Badge className={getPriorityColor(project.priority)}>
        {project.priority.toUpperCase()}
      </Badge>
    )},
    { key: 'progress', header: 'Progress', render: (project: Project) => (
      <div className="w-full bg-gray-200 rounded-full h-2">
        <div
          className="bg-blue-600 h-2 rounded-full"
          style={{ width: `${project.progress}%` }}
        ></div>
      </div>
    )},
    { key: 'budget', header: 'Budget', render: (project: Project) =>
      `$${project.budget?.toLocaleString() || 0}`
    },
    { key: 'actions', header: 'Actions', render: (project: Project) => (
      <div className="flex gap-2">
        <Button variant="outline" size="sm" onClick={() => handleEdit(project)}>
          <Edit className="h-4 w-4" />
        </Button>
        <Button variant="outline" size="sm" onClick={() => handleDelete(project.id)}>
          <Trash2 className="h-4 w-4" />
        </Button>
      </div>
    )}
  ];

  return (
    <EnterpriseLayoutEnhanced>
      <div className="p-6 space-y-6">
        {/* Header */}
        <div className="flex justify-between items-center">
          <div>
            <h1 className="text-3xl font-bold">Projects Management</h1>
            <p className="text-gray-600">Manage and track all your projects</p>
          </div>
          <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
            <DialogTrigger asChild>
              <Button>
                <Plus className="h-4 w-4 mr-2" />
                New Project
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-2xl">
              <DialogHeader>
                <DialogTitle>Create New Project</DialogTitle>
              </DialogHeader>
              <ProjectForm
                formData={formData}
                setFormData={setFormData}
                clients={clients}
                teamMembers={teamMembers}
                onSubmit={handleCreate}
                isLoading={createProjectMutation.isPending}
              />
            </DialogContent>
          </Dialog>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Total Projects</CardTitle>
              <Calendar className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{stats.total}</div>
            </CardContent>
          </Card>
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Active Projects</CardTitle>
              <Users className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{stats.active}</div>
            </CardContent>
          </Card>
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Completed</CardTitle>
              <Eye className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{stats.completed}</div>
            </CardContent>
          </Card>
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Total Budget</CardTitle>
              <DollarSign className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">${stats.totalBudget.toLocaleString()}</div>
            </CardContent>
          </Card>
        </div>

        {/* Filters */}
        <div className="flex gap-4 items-center">
          <div className="relative flex-1">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
            <Input
              placeholder="Search projects..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-10"
            />
          </div>
          <Select value={statusFilter} onValueChange={setStatusFilter}>
            <SelectTrigger className="w-40">
              <SelectValue placeholder="Status" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Status</SelectItem>
              <SelectItem value="planning">Planning</SelectItem>
              <SelectItem value="active">Active</SelectItem>
              <SelectItem value="on_hold">On Hold</SelectItem>
              <SelectItem value="completed">Completed</SelectItem>
              <SelectItem value="cancelled">Cancelled</SelectItem>
            </SelectContent>
          </Select>
          <Select value={priorityFilter} onValueChange={setPriorityFilter}>
            <SelectTrigger className="w-40">
              <SelectValue placeholder="Priority" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Priority</SelectItem>
              <SelectItem value="low">Low</SelectItem>
              <SelectItem value="medium">Medium</SelectItem>
              <SelectItem value="high">High</SelectItem>
              <SelectItem value="critical">Critical</SelectItem>
            </SelectContent>
          </Select>
        </div>

        {/* Projects Table */}
        <Card>
          <CardHeader>
            <CardTitle>Projects ({filteredProjects.length})</CardTitle>
          </CardHeader>
          <CardContent>
            {isLoading ? (
              <div className="text-center py-8">Loading projects...</div>
            ) : (
              <EnterpriseDataGrid
                data={filteredProjects}
                columns={columns}
                searchable={false}
                pagination
                pageSize={10}
              />
            )}
          </CardContent>
        </Card>

        {/* Edit Dialog */}
        {editingProject && (
          <Dialog open={!!editingProject} onOpenChange={() => setEditingProject(null)}>
            <DialogContent className="max-w-2xl">
              <DialogHeader>
                <DialogTitle>Edit Project</DialogTitle>
              </DialogHeader>
              <ProjectForm
                formData={formData}
                setFormData={setFormData}
                clients={clients}
                teamMembers={teamMembers}
                onSubmit={handleUpdate}
                isLoading={updateProjectMutation.isPending}
              />
            </DialogContent>
          </Dialog>
        )}
      </div>
    </EnterpriseLayoutEnhanced>
  );
}

interface ProjectFormProps {
  formData: any;
  setFormData: (data: any) => void;
  clients?: any[];
  teamMembers?: any[];
  onSubmit: () => void;
  isLoading: boolean;
}

function ProjectForm({ formData, setFormData, clients, teamMembers, onSubmit, isLoading }: ProjectFormProps) {
  return (
    <div className="space-y-4">
      <div className="grid grid-cols-2 gap-4">
        <div>
          <label className="block text-sm font-medium mb-1">Project Name</label>
          <Input
            value={formData.name}
            onChange={(e) => setFormData({ ...formData, name: e.target.value })}
            placeholder="Enter project name"
          />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Status</label>
          <Select value={formData.status} onValueChange={(value) => setFormData({ ...formData, status: value })}>
            <SelectTrigger>
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="planning">Planning</SelectItem>
              <SelectItem value="active">Active</SelectItem>
              <SelectItem value="on_hold">On Hold</SelectItem>
              <SelectItem value="completed">Completed</SelectItem>
              <SelectItem value="cancelled">Cancelled</SelectItem>
            </SelectContent>
          </Select>
        </div>
      </div>

      <div>
        <label className="block text-sm font-medium mb-1">Description</label>
        <textarea
          className="w-full p-2 border rounded-md"
          rows={3}
          value={formData.description}
          onChange={(e) => setFormData({ ...formData, description: e.target.value })}
          placeholder="Enter project description"
        />
      </div>

      <div className="grid grid-cols-2 gap-4">
        <div>
          <label className="block text-sm font-medium mb-1">Priority</label>
          <Select value={formData.priority} onValueChange={(value) => setFormData({ ...formData, priority: value })}>
            <SelectTrigger>
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="low">Low</SelectItem>
              <SelectItem value="medium">Medium</SelectItem>
              <SelectItem value="high">High</SelectItem>
              <SelectItem value="critical">Critical</SelectItem>
            </SelectContent>
          </Select>
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Budget</label>
          <Input
            type="number"
            value={formData.budget}
            onChange={(e) => setFormData({ ...formData, budget: parseFloat(e.target.value) || 0 })}
            placeholder="0"
          />
        </div>
      </div>

      <div className="grid grid-cols-2 gap-4">
        <div>
          <label className="block text-sm font-medium mb-1">Start Date</label>
          <Input
            type="date"
            value={formData.start_date}
            onChange={(e) => setFormData({ ...formData, start_date: e.target.value })}
          />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">End Date</label>
          <Input
            type="date"
            value={formData.end_date}
            onChange={(e) => setFormData({ ...formData, end_date: e.target.value })}
          />
        </div>
      </div>

      <div className="grid grid-cols-2 gap-4">
        <div>
          <label className="block text-sm font-medium mb-1">Client</label>
          <Select value={formData.client_id} onValueChange={(value) => setFormData({ ...formData, client_id: value })}>
            <SelectTrigger>
              <SelectValue placeholder="Select client" />
            </SelectTrigger>
            <SelectContent>
              {clients?.map(client => (
                <SelectItem key={client.id} value={client.id}>
                  {client.name}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Project Manager</label>
          <Select value={formData.manager_id} onValueChange={(value) => setFormData({ ...formData, manager_id: value })}>
            <SelectTrigger>
              <SelectValue placeholder="Select manager" />
            </SelectTrigger>
            <SelectContent>
              {teamMembers?.map(member => (
                <SelectItem key={member.id} value={member.id}>
                  {member.name}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
      </div>

      <div>
        <label className="block text-sm font-medium mb-1">Progress (%)</label>
        <Input
          type="number"
          min="0"
          max="100"
          value={formData.progress}
          onChange={(e) => setFormData({ ...formData, progress: parseInt(e.target.value) || 0 })}
          placeholder="0"
        />
      </div>

      <div className="flex justify-end gap-2">
        <Button variant="outline" onClick={() => {}}>
          Cancel
        </Button>
        <Button onClick={onSubmit} disabled={isLoading}>
          {isLoading ? 'Saving...' : 'Save Project'}
        </Button>
      </div>
    </div>
  );
}

