'use client';

import React, { useState, useEffect } from 'react';
import { supabase } from '../../lib/supabaseClient';
import { Card } from';
import LoadingSpinner from '../ui/LoadingSpinner';
import { Plus, Edit, Trash2, BarChart3 } from 'lucide-react';

interface AbTest {
  id: string;
  name: string;
  description: string;
  page_url: string;
  variants: Array<{
    id: string;
    name: string;
    content: Record<string, unknown>;
    traffic_percentage: number;
  }>;
  status: 'active' | 'paused' | 'completed';
  start_date: string;
  end_date?: string;
  created_at: string;
}

const AbTesting: React.FC = () => {
  const [tests, setTests] = useState<AbTest[]>([]);
  const [loading, setLoading] = useState(true);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [selectedTest, setSelectedTest] = useState<AbTest | null>(null);

  useEffect(() => {
    fetchTests();
  }, []);

  const fetchTests = async () => {
    try {
      const { data, error } = await supabase
        .from('ab_tests')
        .select('*')
        .order('created_at', { ascending: false });

      if (error) throw error;
      setTests(data || []);
    } catch (error) {
      console.error('Error fetching A/B tests:', error);
    } finally {
      setLoading(false);
    }
  };

  const createTest = async (testData: Partial<AbTest>) => {
    try {
      const { data, error } = await supabase
        .from('ab_tests')
        .insert([testData])
        .select()
        .single();

      if (error) throw error;
      setTests([data, ...tests]);
      setShowCreateForm(false);
    } catch (error) {
      console.error('Error creating A/B test:', error);
    }
  };

  const updateTestStatus = async (id: string, status: string) => {
    try {
      const { error } = await supabase
        .from('ab_tests')
        .update({ status })
        .eq('id', id);

      if (error) throw error;
      setTests(tests.map(test =>
        test.id === id ? { ...test, status } : test
      ));
    } catch (error) {
      console.error('Error updating test status:', error);
    }
  };

  const deleteTest = async (id: string) => {
    if (!confirm('Are you sure you want to delete this A/B test?')) return;

    try {
      const { error } = await supabase
        .from('ab_tests')
        .delete()
        .eq('id', id);

      if (error) throw error;
      setTests(tests.filter(test => test.id !== id));
    } catch (error) {
      console.error('Error deleting test:', error);
    }
  };

  if (loading) return <LoadingSpinner />;

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold">A/B Testing</h2>
        <button
          onClick={() => setShowCreateForm(true)}
          className="flex items-center space-x-2 px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
        >
          <Plus className="w-4 h-4" />
          <span>Create Test</span>
        </button>
      </div>

      {showCreateForm && (
        <CreateTestForm
          onSubmit={createTest}
          onCancel={() => setShowCreateForm(false)}
        />
      )}

      <div className="grid gap-4">
        {tests.map(test => (
          <Card key={test.id} className="p-6">
            <div className="flex justify-between items-start">
              <div className="flex-1">
                <h3 className="text-lg font-semibold">{test.name}</h3>
                <p className="text-gray-600 dark:text-gray-400 mt-1">{test.description}</p>
                <p className="text-sm text-gray-500 mt-2">Page: {test.page_url}</p>
                <div className="flex items-center space-x-4 mt-2">
                  <span className={`px-2 py-1 rounded text-xs ${
                    test.status === 'active' ? 'bg-green-100 text-green-800' :
                    test.status === 'paused' ? 'bg-yellow-100 text-yellow-800' :
                    'bg-gray-100 text-gray-800'
                  }`}>
                    {test.status}
                  </span>
                  <span className="text-sm text-gray-500">
                    Started: {new Date(test.start_date).toLocaleDateString()}
                  </span>
                </div>
              </div>
              <div className="flex items-center space-x-2">
                <button
                  onClick={() => setSelectedTest(test)}
                  className="p-2 text-gray-500 hover:text-blue-500"
                >
                  <BarChart3 className="w-4 h-4" />
                </button>
                <button
                  onClick={() => updateTestStatus(test.id, test.status === 'active' ? 'paused' : 'active')}
                  className="p-2 text-gray-500 hover:text-yellow-500"
                >
                  <Edit className="w-4 h-4" />
                </button>
                <button
                  onClick={() => deleteTest(test.id)}
                  className="p-2 text-gray-500 hover:text-red-500"
                >
                  <Trash2 className="w-4 h-4" />
                </button>
              </div>
            </div>
          </Card>
        ))}
      </div>

      {selectedTest && (
        <TestResultsModal
          test={selectedTest}
          onClose={() => setSelectedTest(null)}
        />
      )}
    </div>
  );
};

const CreateTestForm: React.FC<{
  onSubmit: (data: Partial<AbTest>) => void;
  onCancel: () => void;
}> = ({ onSubmit, onCancel }) => {
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    page_url: '',
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSubmit({
      ...formData,
      status: 'active',
      start_date: new Date().toISOString(),
      variants: [
        { id: '1', name: 'Control', content: {}, traffic_percentage: 50 },
        { id: '2', name: 'Variant A', content: {}, traffic_percentage: 50 },
      ],
    });
  };

  return (
    <Card className="p-6">
      <h3 className="text-lg font-semibold mb-4">Create A/B Test</h3>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block text-sm font-medium mb-2">Test Name</label>
          <input
            type="text"
            value={formData.name}
            onChange={(e) => setFormData({ ...formData, name: e.target.value })}
            className="w-full px-3 py-2 border rounded-lg dark:bg-gray-800 dark:border-gray-600"
            required
          />
        </div>
        <div>
          <label className="block text-sm font-medium mb-2">Description</label>
          <textarea
            value={formData.description}
            onChange={(e) => setFormData({ ...formData, description: e.target.value })}
            rows={3}
            className="w-full px-3 py-2 border rounded-lg dark:bg-gray-800 dark:border-gray-600"
          />
        </div>
        <div>
          <label className="block text-sm font-medium mb-2">Page URL</label>
          <input
            type="url"
            value={formData.page_url}
            onChange={(e) => setFormData({ ...formData, page_url: e.target.value })}
            className="w-full px-3 py-2 border rounded-lg dark:bg-gray-800 dark:border-gray-600"
            required
          />
        </div>
        <div className="flex justify-end space-x-2">
          <button
            type="button"
            onClick={onCancel}
            className="px-4 py-2 text-gray-600 hover:text-gray-800"
          >
            Cancel
          </button>
          <button
            type="submit"
            className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
          >
            Create Test
          </button>
        </div>
      </form>
    </Card>
  );
};

const TestResultsModal: React.FC<{
  test: AbTest;
  onClose: () => void;
}> = ({ test, onClose }) => {
  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <Card className="p-6 max-w-2xl w-full max-h-[80vh] overflow-y-auto">
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-lg font-semibold">Test Results: {test.name}</h3>
          <button onClick={onClose} className="text-gray-500 hover:text-gray-700">
            ×
          </button>
        </div>
        <div className="space-y-4">
          {test.variants.map(variant => (
            <div key={variant.id} className="border rounded-lg p-4">
              <h4 className="font-medium">{variant.name}</h4>
              <p className="text-sm text-gray-500">Traffic: {variant.traffic_percentage}%</p>
              <div className="mt-2">
                <p className="text-sm">Views: 0 | Conversions: 0 | Conversion Rate: 0%</p>
              </div>
            </div>
          ))}
        </div>
        <div className="mt-4 text-center text-gray-500">
          Results will be displayed here once the test has collected data.
        </div>
      </Card>
    </div>
  );
};

export default AbTesting;

'use client';

import React, { useState, useEffect } from 'react';
import { Card } from';
import LoadingSpinner from '../ui/LoadingSpinner';
import { Users, MessageSquare, Clock, ThumbsUp } from 'lucide-react';

interface ChatbotStats {
  totalConversations: number;
  totalMessages: number;
  averageSessionDuration: number;
  topQueries: Array<{ query: string; count: number }>;
  userSatisfaction: number;
  dailyStats: Array<{ date: string; conversations: number; messages: number }>;
}

const ChatbotAnalytics: React.FC = () => {
  const [stats, setStats] = useState<ChatbotStats | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [timeRange, setTimeRange] = useState<'7d' | '30d' | '90d'>('30d');

  useEffect(() => {
    const fetchAnalytics = async () => {
      setLoading(true);
      try {
        const response = await fetch(`/api/analytics/chatbot?range=${timeRange}`);
        if (!response.ok) throw new Error('Failed to fetch analytics');
        const data = await response.json();
        setStats(data);
      } catch (err: unknown) {
        const errorMessage = err instanceof Error ? err.message : 'Failed to fetch analytics';
        setError(errorMessage);
      } finally {
        setLoading(false);
      }
    };

    fetchAnalytics();
  }, [timeRange]);

  if (loading) return <LoadingSpinner />;
  if (error) return <div className="text-red-500">Error: {error}</div>;
  if (!stats) return <div>No data available</div>;

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold">Chatbot Analytics</h2>
        <select
          value={timeRange}
          onChange={(e) => setTimeRange(e.target.value as '7d' | '30d' | '90d')}
          className="px-3 py-2 border rounded-lg dark:bg-gray-800 dark:border-gray-600"
        >
          <option value="7d">Last 7 days</option>
          <option value="30d">Last 30 days</option>
          <option value="90d">Last 90 days</option>
        </select>
      </div>

      {/* Key Metrics */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <Card className="p-6">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-500 dark:text-gray-400">Total Conversations</p>
              <p className="text-3xl font-bold">{stats.totalConversations}</p>
            </div>
            <MessageSquare className="w-8 h-8 text-blue-500" />
          </div>
        </Card>

        <Card className="p-6">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-500 dark:text-gray-400">Total Messages</p>
              <p className="text-3xl font-bold">{stats.totalMessages}</p>
            </div>
            <Users className="w-8 h-8 text-green-500" />
          </div>
        </Card>

        <Card className="p-6">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-500 dark:text-gray-400">Avg Session Duration</p>
              <p className="text-3xl font-bold">{Math.round(stats.averageSessionDuration)}m</p>
            </div>
            <Clock className="w-8 h-8 text-yellow-500" />
          </div>
        </Card>

        <Card className="p-6">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-500 dark:text-gray-400">User Satisfaction</p>
              <p className="text-3xl font-bold">{stats.userSatisfaction}%</p>
            </div>
            <ThumbsUp className="w-8 h-8 text-purple-500" />
          </div>
        </Card>
      </div>

      {/* Top Queries */}
      <Card className="p-6">
        <h3 className="text-lg font-semibold mb-4">Top Queries</h3>
        <div className="space-y-3">
          {stats.topQueries.map((query, index) => (
            <div key={index} className="flex items-center justify-between">
              <span className="text-sm">{query.query}</span>
              <span className="text-sm font-medium bg-blue-100 dark:bg-blue-900 text-blue-800 dark:text-blue-200 px-2 py-1 rounded">
                {query.count}
              </span>
            </div>
          ))}
        </div>
      </Card>

      {/* Daily Stats Chart Placeholder */}
      <Card className="p-6">
        <h3 className="text-lg font-semibold mb-4">Daily Activity</h3>
        <div className="h-64 flex items-center justify-center text-gray-500">
          Chart visualization would be implemented here
        </div>
      </Card>
    </div>
  );
};

export default ChatbotAnalytics;

'use client';

import React, { useState, useEffect } from 'react';
import { supabase } from '../../lib/supabaseClient';
import { Card } from';
import LoadingSpinner from '../ui/LoadingSpinner';

interface ChatbotConfig {
  id: string;
  personality: 'professional' | 'friendly' | 'technical' | 'creative';
  tone: 'formal' | 'casual' | 'enthusiastic' | 'calm';
  language: string;
  max_tokens: number;
  temperature: number;
  system_prompt: string;
  welcome_message: string;
  fallback_message: string;
  enabled_features: string[];
}

const ChatbotSettings: React.FC = () => {
  const [config, setConfig] = useState<ChatbotConfig | null>(null);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    fetchConfig();
  }, []);

  const fetchConfig = async () => {
    try {
      const { data, error } = await supabase
        .from('chatbot_config')
        .select('*')
        .single();

      if (error && error.code !== 'PGRST116') throw error;

      if (data) {
        setConfig(data);
      } else {
        // Create default config
        const defaultConfig: Omit<ChatbotConfig, 'id'> = {
          personality: 'professional',
          tone: 'formal',
          language: 'en',
          max_tokens: 1000,
          temperature: 0.7,
          system_prompt: 'You are Auralis, an AI assistant for Limitless Infotech. You help users learn about our services, portfolio, and how we can help their business.',
          welcome_message: "Hello! I'm Auralis, your AI-powered assistant from Limitless Infotech. How can I help you today?",
          fallback_message: "I'm sorry, I didn't understand that. Could you please rephrase your question?",
          enabled_features: ['voice_input', 'suggestions', 'analytics'],
        };

        const { data: newConfig, error: insertError } = await supabase
          .from('chatbot_config')
          .insert(defaultConfig)
          .select()
          .single();

        if (insertError) throw insertError;
        setConfig(newConfig);
      }
    } catch (err: unknown) {
      setError(err instanceof Error ? err.message : 'Failed to fetch config');
    } finally {
      setLoading(false);
    }
  };

  const handleSave = async () => {
    if (!config) return;
    setSaving(true);
    try {
      const { error } = await supabase
        .from('chatbot_config')
        .update({
          personality: config.personality,
          tone: config.tone,
          language: config.language,
          max_tokens: config.max_tokens,
          temperature: config.temperature,
          system_prompt: config.system_prompt,
          welcome_message: config.welcome_message,
          fallback_message: config.fallback_message,
          enabled_features: config.enabled_features,
        })
        .eq('id', config.id);

      if (error) throw error;
      setError(null);
    } catch (err: unknown) {
      setError(err instanceof Error ? err.message : 'Failed to save config');
    } finally {
      setSaving(false);
    }
  };

  const updateConfig = (field: keyof ChatbotConfig, value: unknown) => {
    if (!config) return;
    setConfig({ ...config, [field]: value });
  };

  const toggleFeature = (feature: string) => {
    if (!config) return;
    const enabled = config.enabled_features.includes(feature);
    const newFeatures = enabled
      ? config.enabled_features.filter(f => f !== feature)
      : [...config.enabled_features, feature];
    updateConfig('enabled_features', newFeatures);
  };

  if (loading) return <LoadingSpinner />;
  if (!config) return <div>Failed to load configuration</div>;

  return (
    <div className="space-y-6">
      <div>
        <h2 className="text-2xl font-bold mb-2">Chatbot Settings</h2>
        <p className="text-gray-600 dark:text-gray-400">
          Configure the personality, behavior, and features of your AI chatbot.
        </p>
      </div>

      {error && (
        <div className="bg-red-50 dark:bg-red-900/20 border border-red-200 dark:border-red-800 rounded-lg p-4">
          <p className="text-red-800 dark:text-red-200">{error}</p>
        </div>
      )}

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Personality Settings */}
        <Card className="p-6">
          <h3 className="text-lg font-semibold mb-4">Personality & Tone</h3>
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium mb-2">Personality</label>
              <select
                value={config.personality}
                onChange={(e) => updateConfig('personality', e.target.value)}
                className="w-full p-2 border rounded-lg dark:bg-gray-800 dark:border-gray-600"
              >
                <option value="professional">Professional</option>
                <option value="friendly">Friendly</option>
                <option value="technical">Technical</option>
                <option value="creative">Creative</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium mb-2">Tone</label>
              <select
                value={config.tone}
                onChange={(e) => updateConfig('tone', e.target.value)}
                className="w-full p-2 border rounded-lg dark:bg-gray-800 dark:border-gray-600"
              >
                <option value="formal">Formal</option>
                <option value="casual">Casual</option>
                <option value="enthusiastic">Enthusiastic</option>
                <option value="calm">Calm</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium mb-2">Language</label>
              <select
                value={config.language}
                onChange={(e) => updateConfig('language', e.target.value)}
                className="w-full p-2 border rounded-lg dark:bg-gray-800 dark:border-gray-600"
              >
                <option value="en">English</option>
                <option value="es">Spanish</option>
                <option value="fr">French</option>
                <option value="de">German</option>
                <option value="hi">Hindi</option>
              </select>
            </div>
          </div>
        </Card>

        {/* AI Parameters */}
        <Card className="p-6">
          <h3 className="text-lg font-semibold mb-4">AI Parameters</h3>
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium mb-2">
                Max Tokens: {config.max_tokens}
              </label>
              <input
                type="range"
                min="100"
                max="2000"
                step="50"
                value={config.max_tokens}
                onChange={(e) => updateConfig('max_tokens', parseInt(e.target.value))}
                className="w-full"
              />
            </div>

            <div>
              <label className="block text-sm font-medium mb-2">
                Temperature: {config.temperature}
              </label>
              <input
                type="range"
                min="0"
                max="1"
                step="0.1"
                value={config.temperature}
                onChange={(e) => updateConfig('temperature', parseFloat(e.target.value))}
                className="w-full"
              />
              <p className="text-xs text-gray-500 mt-1">
                Lower = more focused, Higher = more creative
              </p>
            </div>
          </div>
        </Card>

        {/* Messages */}
        <Card className="p-6 lg:col-span-2">
          <h3 className="text-lg font-semibold mb-4">Messages</h3>
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium mb-2">Welcome Message</label>
              <textarea
                value={config.welcome_message}
                onChange={(e) => updateConfig('welcome_message', e.target.value)}
                rows={2}
                className="w-full p-2 border rounded-lg dark:bg-gray-800 dark:border-gray-600"
                placeholder="Enter welcome message..."
              />
            </div>

            <div>
              <label className="block text-sm font-medium mb-2">Fallback Message</label>
              <textarea
                value={config.fallback_message}
                onChange={(e) => updateConfig('fallback_message', e.target.value)}
                rows={2}
                className="w-full p-2 border rounded-lg dark:bg-gray-800 dark:border-gray-600"
                placeholder="Enter fallback message..."
              />
            </div>

            <div>
              <label className="block text-sm font-medium mb-2">System Prompt</label>
              <textarea
                value={config.system_prompt}
                onChange={(e) => updateConfig('system_prompt', e.target.value)}
                rows={4}
                className="w-full p-2 border rounded-lg dark:bg-gray-800 dark:border-gray-600"
                placeholder="Enter system prompt..."
              />
            </div>
          </div>
        </Card>

        {/* Features */}
        <Card className="p-6 lg:col-span-2">
          <h3 className="text-lg font-semibold mb-4">Enabled Features</h3>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            {[
              { key: 'voice_input', label: 'Voice Input' },
              { key: 'suggestions', label: 'Suggestions' },
              { key: 'analytics', label: 'Analytics' },
              { key: 'multilingual', label: 'Multilingual' },
              { key: 'memory', label: 'Conversation Memory' },
              { key: 'feedback', label: 'User Feedback' },
            ].map(({ key, label }) => (
              <label key={key} className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  checked={config.enabled_features.includes(key)}
                  onChange={() => toggleFeature(key)}
                  className="rounded"
                />
                <span className="text-sm">{label}</span>
              </label>
            ))}
          </div>
        </Card>
      </div>

      <div className="flex justify-end">
        <button
          onClick={handleSave}
          disabled={saving}
          className="bg-blue-500 text-white px-6 py-2 rounded-lg hover:bg-blue-600 disabled:opacity-50 disabled:cursor-not-allowed"
        >
          {saving ? 'Saving...' : 'Save Settings'}
        </button>
      </div>
    </div>
  );
};

export default ChatbotSettings;

'use client';

import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import { Monitor, MapPin, Clock, User, AlertTriangle, Trash2 } from 'lucide-react';

interface Session {
  id: string;
  user_id: string;
  device_fingerprint: string | null;
  ip_address: string | null;
  user_agent: string | null;
  geo_location: any;
  created_at: string;
  expires_at: string;
  last_activity: string;
  is_active: boolean;
  profiles: {
    email: string;
    role: string;
  };
}

const SessionsManagement: React.FC = () => {
  const [sessions, setSessions] = useState<Session[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [terminatingSessionId, setTerminatingSessionId] = useState<string | null>(null);

  const fetchSessions = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch('/api/admin/sessions');
      if (!response.ok) {
        throw new Error('Failed to fetch sessions');
      }
      const data = await response.json();
      setSessions(data.sessions);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'An error occurred');
    } finally {
      setLoading(false);
    }
  };

  const terminateSession = async (sessionId: string) => {
    setTerminatingSessionId(sessionId);
    try {
      const response = await fetch('/api/admin/session-terminate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ sessionId }),
      });

      if (!response.ok) {
        throw new Error('Failed to terminate session');
      }

      // Refresh sessions list
      await fetchSessions();
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to terminate session');
    } finally {
      setTerminatingSessionId(null);
    }
  };



  const getTimeAgo = (dateString: string) => {
    const now = new Date();
    const date = new Date(dateString);
    const diffMs = now.getTime() - date.getTime();
    const diffMins = Math.floor(diffMs / (1000 * 60));

    if (diffMins < 1) return 'Just now';
    if (diffMins < 60) return `${diffMins}m ago`;
    const diffHours = Math.floor(diffMins / 60);
    if (diffHours < 24) return `${diffHours}h ago`;
    const diffDays = Math.floor(diffHours / 24);
    return `${diffDays}d ago`;
  };

  useEffect(() => {
    fetchSessions();
  }, []);

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-gray-900 dark:text-white">Active Sessions</h2>
          <p className="text-gray-600 dark:text-gray-400">Monitor and manage admin sessions</p>
        </div>
        <button
          onClick={fetchSessions}
          disabled={loading}
          className="px-4 py-2 bg-accent text-white rounded-lg hover:bg-accent-dark transition-colors disabled:opacity-50"
        >
          {loading ? 'Refreshing...' : 'Refresh'}
        </button>
      </div>

      {error && (
        <div className="p-4 bg-red-50 dark:bg-red-900/20 border border-red-200 dark:border-red-800 rounded-lg">
          <div className="flex items-center">
            <AlertTriangle className="w-5 h-5 text-red-600 dark:text-red-400 mr-2" />
            <span className="text-red-800 dark:text-red-200">{error}</span>
          </div>
        </div>
      )}

      <div className="grid gap-4">
        {sessions.map((session) => (
          <motion.div
            key={session.id}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            className="bg-white dark:bg-gray-800 rounded-lg shadow-sm border border-gray-200 dark:border-gray-700 p-6"
          >
            <div className="flex items-start justify-between">
              <div className="flex-1">
                <div className="flex items-center space-x-3 mb-3">
                  <div className="w-10 h-10 bg-accent rounded-full flex items-center justify-center">
                    <Monitor className="w-5 h-5 text-white" />
                  </div>
                  <div>
                    <h3 className="font-semibold text-gray-900 dark:text-white">
                      {session.profiles.email}
                    </h3>
                    <p className="text-sm text-gray-600 dark:text-gray-400">
                      {session.profiles.role}
                    </p>
                  </div>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 text-sm">
                  <div className="flex items-center space-x-2">
                    <MapPin className="w-4 h-4 text-gray-400" />
                    <span className="text-gray-600 dark:text-gray-400">
                      {session.ip_address || 'Unknown IP'}
                    </span>
                  </div>

                  <div className="flex items-center space-x-2">
                    <Clock className="w-4 h-4 text-gray-400" />
                    <span className="text-gray-600 dark:text-gray-400">
                      Created {getTimeAgo(session.created_at)}
                    </span>
                  </div>

                  <div className="flex items-center space-x-2">
                    <User className="w-4 h-4 text-gray-400" />
                    <span className="text-gray-600 dark:text-gray-400">
                      Active {getTimeAgo(session.last_activity)}
                    </span>
                  </div>
                </div>

                {session.user_agent && (
                  <div className="mt-3 p-3 bg-gray-50 dark:bg-gray-700 rounded-lg">
                    <p className="text-xs text-gray-600 dark:text-gray-400 font-mono">
                      {session.user_agent}
                    </p>
                  </div>
                )}
              </div>

              <button
                onClick={() => terminateSession(session.id)}
                disabled={terminatingSessionId === session.id}
                className="ml-4 p-2 text-red-600 hover:bg-red-50 dark:hover:bg-red-900/20 rounded-lg transition-colors disabled:opacity-50"
                title="Terminate Session"
              >
                {terminatingSessionId === session.id ? (
                  <div className="w-4 h-4 border-2 border-red-600 border-t-transparent rounded-full animate-spin" />
                ) : (
                  <Trash2 className="w-4 h-4" />
                )}
              </button>
            </div>
          </motion.div>
        ))}
      </div>

      {sessions.length === 0 && !loading && (
        <div className="text-center py-12">
          <Monitor className="w-12 h-12 text-gray-400 mx-auto mb-4" />
          <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-2">No Active Sessions</h3>
          <p className="text-gray-600 dark:text-gray-400">No admin sessions are currently active.</p>
        </div>
      )}
    </div>
  );
};

export default SessionsManagement;

'use client';

import React, { useState, useEffect, useCallback } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, PieChart, Pie, Cell } from 'recharts';
import { TrendingUp, Star, CheckCircle, Clock } from 'lucide-react';
import { Card } from';

interface AnalyticsData {
  totalTestimonials: number;
  approvedTestimonials: number;
  unapprovedTestimonials: number;
  ratingDistribution: Record<string, number>;
  averageRating: number;
  topCompanies: { company: string; count: number }[];
  approvalRate: number;
  testimonialsTrend: { month: string; count: number }[];
}

const TestimonialAnalytics: React.FC = () => {
  const [analyticsData, setAnalyticsData] = useState<AnalyticsData | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [dateRange, setDateRange] = useState<{ start: string; end: string }>({
    start: '',
    end: ''
  });

  const fetchAnalyticsData = useCallback(async () => {
    try {
      setLoading(true);
      const queryParams = new URLSearchParams();
      
      if (dateRange.start) {
        queryParams.append('startDate', dateRange.start);
      }
      
      if (dateRange.end) {
        queryParams.append('endDate', dateRange.end);
      }
      
      const response = await fetch(`/api/testimonials/analytics?${queryParams.toString()}`);
      if (!response.ok) throw new Error('Failed to fetch analytics data');
      
      const data: AnalyticsData = await response.json();
      setAnalyticsData(data);
    } catch (err: unknown) {
      setError(err instanceof Error ? err.message : 'Failed to fetch analytics data');
    } finally {
      setLoading(false);
    }
  }, [dateRange]);

  useEffect(() => {
    fetchAnalyticsData();
  }, [fetchAnalyticsData]);

  const handleDateChange = (type: 'start' | 'end', value: string) => {
    setDateRange(prev => ({
      ...prev,
      [type]: value
    }));
  };

  const resetFilters = () => {
    setDateRange({ start: '', end: '' });
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-accent"></div>
      </div>
    );
  }

  if (error) {
    return (
      <Card className="p-6">
        <div className="text-red-500 text-center">
          Error loading analytics: {error}
        </div>
      </Card>
    );
  }

  if (!analyticsData) {
    return (
      <Card className="p-6">
        <div className="text-center">
          No analytics data available
        </div>
      </Card>
    );
  }

  // Prepare data for charts
  const ratingData = Object.entries(analyticsData.ratingDistribution).map(([rating, count]) => ({
    rating: `${rating} Stars`,
    count
  }));

  const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884D8'];

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h1 className="text-3xl font-bold">Testimonial Analytics</h1>
      </div>

      {/* Date Range Filter */}
      <Card className="p-4">
        <div className="flex flex-col md:flex-row gap-4 items-end">
          <div>
            <label className="block text-sm font-medium mb-1">Start Date</label>
            <input
              type="date"
              value={dateRange.start}
              onChange={(e) => handleDateChange('start', e.target.value)}
              className="px-3 py-2 border rounded-lg dark:bg-gray-800 dark:border-gray-600"
            />
          </div>
          <div>
            <label className="block text-sm font-medium mb-1">End Date</label>
            <input
              type="date"
              value={dateRange.end}
              onChange={(e) => handleDateChange('end', e.target.value)}
              className="px-3 py-2 border rounded-lg dark:bg-gray-800 dark:border-gray-600"
            />
          </div>
          <button
            onClick={resetFilters}
            className="px-4 py-2 bg-gray-200 dark:bg-gray-700 text-gray-800 dark:text-gray-200 rounded-lg hover:bg-gray-300 dark:hover:bg-gray-600"
          >
            Reset Filters
          </button>
        </div>
      </Card>

      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <Card className="p-6 bg-gradient-to-br from-blue-50 to-blue-100 dark:from-blue-900/30 dark:to-blue-800/30">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-gray-600 dark:text-gray-300">Total Testimonials</p>
              <p className="text-3xl font-bold text-blue-600 dark:text-blue-400">{analyticsData.totalTestimonials}</p>
            </div>
            <div className="p-3 bg-blue-100 dark:bg-blue-900/50 rounded-full">
              <TrendingUp className="w-6 h-6 text-blue-600 dark:text-blue-400" />
            </div>
          </div>
        </Card>
        
        <Card className="p-6 bg-gradient-to-br from-green-50 to-green-100 dark:from-green-900/30 dark:to-green-800/30">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-gray-600 dark:text-gray-300">Approved</p>
              <p className="text-3xl font-bold text-green-600 dark:text-green-400">{analyticsData.approvedTestimonials}</p>
            </div>
            <div className="p-3 bg-green-100 dark:bg-green-900/50 rounded-full">
              <CheckCircle className="w-6 h-6 text-green-600 dark:text-green-400" />
            </div>
          </div>
        </Card>
        
        <Card className="p-6 bg-gradient-to-br from-yellow-50 to-yellow-100 dark:from-yellow-900/30 dark:to-yellow-800/30">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-gray-600 dark:text-gray-300">Pending Approval</p>
              <p className="text-3xl font-bold text-yellow-600 dark:text-yellow-400">{analyticsData.unapprovedTestimonials}</p>
            </div>
            <div className="p-3 bg-yellow-100 dark:bg-yellow-900/50 rounded-full">
              <Clock className="w-6 h-6 text-yellow-600 dark:text-yellow-400" />
            </div>
          </div>
        </Card>
        
        <Card className="p-6 bg-gradient-to-br from-purple-50 to-purple-100 dark:from-purple-900/30 dark:to-purple-800/30">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-gray-600 dark:text-gray-300">Average Rating</p>
              <p className="text-3xl font-bold text-purple-600 dark:text-purple-400">{analyticsData.averageRating}</p>
            </div>
            <div className="p-3 bg-purple-100 dark:bg-purple-900/50 rounded-full">
              <Star className="w-6 h-6 text-purple-600 dark:text-purple-400" />
            </div>
          </div>
        </Card>
      </div>

      {/* Charts */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Rating Distribution */}
        <Card className="p-6">
          <h2 className="text-xl font-bold mb-4">Rating Distribution</h2>
          <div className="h-80">
            <ResponsiveContainer width="100%" height="100%">
              <PieChart>
                <Pie
                  data={ratingData}
                  cx="50%"
                  cy="50%"
                  labelLine={false}
                  outerRadius={80}
                  fill="#8884d8"
                  dataKey="count"
                >
                  {ratingData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                  ))}
                </Pie>
                <Tooltip />
                <Legend />
              </PieChart>
            </ResponsiveContainer>
