import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { 
  ChevronLeft, 
  ChevronRight, 
  Calendar as CalendarIcon,
  Clock,
  MapPin,
  Plus,
  Filter,
  Search
} from 'lucide-react';
import Button from '../ui/Button';
import { Card } from';

interface CalendarEvent {
  id: string;
  title: string;
  date: Date;
  startTime?: string;
  endTime?: string;
  location?: string;
  description?: string;
  color?: string;
  allDay?: boolean;
}

interface EnterpriseCalendarProps {
  events?: CalendarEvent[];
  onEventClick?: (event: CalendarEvent) => void;
  onEventAdd?: (date: Date) => void;
  onDateChange?: (date: Date) => void;
  className?: string;
  title?: string;
  showNavigation?: boolean;
  showEventDetails?: boolean;
}

const EnterpriseCalendar: React.FC<EnterpriseCalendarProps> = ({
  events = [],
  onEventClick,
  onEventAdd,
  onDateChange,
  className = '',
  title = 'Calendar',
  showNavigation = true,
  showEventDetails = true
}) => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState<Date | null>(null);

  const getColorClass = (color: string = 'blue') => {
    const colorMap: Record<string, string> = {
      blue: 'bg-blue-500',
      green: 'bg-green-500',
      red: 'bg-red-500',
      yellow: 'bg-yellow-500',
      purple: 'bg-purple-500',
      pink: 'bg-pink-500',
      indigo: 'bg-indigo-500'
    };
    return colorMap[color] || colorMap.blue;
  };

  const navigateMonth = (direction: 'prev' | 'next') => {
    const newDate = new Date(currentDate);
    if (direction === 'prev') {
      newDate.setMonth(newDate.getMonth() - 1);
    } else {
      newDate.setMonth(newDate.getMonth() + 1);
    }
    setCurrentDate(newDate);
    if (onDateChange) {
      onDateChange(newDate);
    }
  };

  const getDaysInMonth = (year: number, month: number) => {
    return new Date(year, month + 1, 0).getDate();
  };

  const getFirstDayOfMonth = (year: number, month: number) => {
    return new Date(year, month, 1).getDay();
  };

  const isSameDay = (date1: Date, date2: Date) => {
    return date1.getDate() === date2.getDate() &&
           date1.getMonth() === date2.getMonth() &&
           date1.getFullYear() === date2.getFullYear();
  };

  const isToday = (date: Date) => {
    const today = new Date();
    return isSameDay(date, today);
  };

  const getEventsForDate = (date: Date) => {
    return events.filter(event => isSameDay(new Date(event.date), date));
  };

  const renderCalendarDays = () => {
    const year = currentDate.getFullYear();
    const month = currentDate.getMonth();
    const daysInMonth = getDaysInMonth(year, month);
    const firstDayOfMonth = getFirstDayOfMonth(year, month);
    
    const days = [];
    
    // Previous month's days
    const prevMonth = new Date(year, month - 1, 1);
    const daysInPrevMonth = getDaysInMonth(prevMonth.getFullYear(), prevMonth.getMonth());
    
    for (let i = firstDayOfMonth - 1; i >= 0; i--) {
      const day = daysInPrevMonth - i;
      days.push(
        <div 
          key={`prev-${day}`} 
          className="p-2 text-center text-gray-400 dark:text-gray-500 text-sm"
        >
          {day}
        </div>
      );
    }
    
    // Current month's days
    for (let day = 1; day <= daysInMonth; day++) {
      const date = new Date(year, month, day);
      const dayEvents = getEventsForDate(date);
      const isSelected = selectedDate && isSameDay(date, selectedDate);
      const isCurrentDay = isToday(date);
      
      days.push(
        <motion.div
          key={`current-${day}`}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          className={`
            p-2 h-24 border border-gray-200 dark:border-gray-700 cursor-pointer transition-colors
            ${isSelected ? 'bg-accent/10' : ''}
            ${isCurrentDay ? 'bg-blue-50 dark:bg-blue-900/20' : ''}
          `}
          onClick={() => {
            setSelectedDate(date);
            if (onEventAdd) {
              onEventAdd(date);
            }
          }}
        >
          <div className={`text-right text-sm ${isCurrentDay ? 'font-bold text-blue-600 dark:text-blue-400' : 'text-gray-900 dark:text-white'}`}>
            {day}
          </div>
          <div className="mt-1 space-y-1 max-h-16 overflow-y-auto">
            {dayEvents.slice(0, 3).map(event => (
              <div
                key={event.id}
                className={`text-xs p-1 rounded truncate text-white ${getColorClass(event.color)}`}
                onClick={(e) => {
                  e.stopPropagation();
                  if (onEventClick) {
                    onEventClick(event);
                  }
                }}
              >
                {event.title}
              </div>
            ))}
            {dayEvents.length > 3 && (
              <div className="text-xs text-gray-500 dark:text-gray-400">
                +{dayEvents.length - 3} more
              </div>
            )}
          </div>
        </motion.div>
      );
    }
    
    // Next month's days
    const totalCells = 42; // 6 rows * 7 days
    
    for (let day = 1; day <= totalCells - days.length; day++) {
      days.push(
        <div 
          key={`next-${day}`} 
          className="p-2 text-center text-gray-400 dark:text-gray-500 text-sm"
        >
          {day}
        </div>
      );
    }
    
    return days;
  };

  const selectedDateEvents = selectedDate ? getEventsForDate(selectedDate) : [];

  return (
    <div className={className}>
      <Card className="p-6">
        {/* Header */}
        <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-4 mb-6">
          <div>
            <h2 className="text-xl font-bold text-gray-900 dark:text-white">{title}</h2>
            <p className="text-sm text-gray-600 dark:text-gray-400 mt-1">
              {currentDate.toLocaleDateString('en-US', { month: 'long', year: 'numeric' })}
            </p>
          </div>
          
          <div className="flex items-center space-x-3">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
              <input
                type="text"
                placeholder="Search events..."
                className="pl-10 pr-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-white focus:ring-accent focus:border-accent"
              />
            </div>
            
            <Button variant="outline" size="sm" icon={<Filter className="w-4 h-4" />}>
              Filter
            </Button>
            
            {onEventAdd && (
              <Button 
                variant="primary" 
                size="sm" 
                icon={<Plus className="w-4 h-4" />}
                onClick={() => onEventAdd(new Date())}
              >
                Add Event
              </Button>
            )}
          </div>
        </div>
        
        {/* Calendar Navigation */}
        {showNavigation && (
          <div className="flex items-center justify-between mb-4">
            <Button 
              variant="outline" 
              size="sm" 
              icon={<ChevronLeft className="w-4 h-4" />}
              onClick={() => navigateMonth('prev')}
            >
              Previous
            </Button>
            
            <Button 
              variant="ghost" 
              size="sm" 
              icon={<CalendarIcon className="w-4 h-4" />}
              onClick={() => setCurrentDate(new Date())}
            >
              Today
            </Button>
            
            <Button 
              variant="outline" 
              size="sm" 
              icon={<ChevronRight className="w-4 h-4" />}
              onClick={() => navigateMonth('next')}
            >
              Next
            </Button>
          </div>
        )}
        
        {/* Calendar Grid */}
        <div className="grid grid-cols-7 gap-1 mb-4">
          {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map(day => (
            <div 
              key={day} 
              className="p-2 text-center text-sm font-medium text-gray-700 dark:text-gray-300 bg-gray-50 dark:bg-gray-800"
            >
              {day}
            </div>
          ))}
          {renderCalendarDays()}
        </div>
      </Card>
      
      {/* Event Details Panel */}
      {showEventDetails && selectedDate && (
        <Card className="mt-6 p-6">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
              Events for {selectedDate.toLocaleDateString('en-US', { weekday: 'long', month: 'long', day: 'numeric' })}
            </h3>
            <span className="text-sm text-gray-500 dark:text-gray-400">
              {selectedDateEvents.length} events
            </span>
          </div>
          
          {selectedDateEvents.length === 0 ? (
            <div className="text-center py-8">
              <CalendarIcon className="w-12 h-12 text-gray-400 mx-auto" />
              <p className="mt-2 text-gray-500 dark:text-gray-400">
                No events scheduled for this day
              </p>
              {onEventAdd && (
                <Button 
                  variant="primary" 
                  className="mt-4" 
                  onClick={() => onEventAdd(selectedDate)}
                >
                  Add Event
                </Button>
              )}
            </div>
          ) : (
            <div className="space-y-4">
              {selectedDateEvents.map(event => (
                <div 
                  key={event.id} 
                  className="p-4 border border-gray-200 dark:border-gray-700 rounded-lg hover:bg-gray-50 dark:hover:bg-gray-800/50 cursor-pointer transition-colors"
                  onClick={() => onEventClick && onEventClick(event)}
                >
                  <div className="flex items-start">
                    <div className={`w-3 h-3 rounded-full mt-1.5 ${getColorClass(event.color)}`}></div>
                    <div className="ml-3 flex-1">
                      <h4 className="font-medium text-gray-900 dark:text-white">{event.title}</h4>
                      <div className="mt-1 space-y-1">
                        {event.allDay ? (
                          <p className="text-sm text-gray-600 dark:text-gray-400">All day</p>
                        ) : (
                          <div className="flex items-center text-sm text-gray-600 dark:text-gray-400">
                            <Clock className="w-4 h-4 mr-1" />
                            <span>{event.startTime} - {event.endTime}</span>
                          </div>
                        )}
                        {event.location && (
                          <div className="flex items-center text-sm text-gray-600 dark:text-gray-400">
                            <MapPin className="w-4 h-4 mr-1" />
                            <span>{event.location}</span>
                          </div>
                        )}
                      </div>
                      {event.description && (
                        <p className="mt-2 text-sm text-gray-600 dark:text-gray-400">
                          {event.description}
                        </p>
                      )}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </Card>
      )}
    </div>
  );
};

export default EnterpriseCalendar;
export type { CalendarEvent };

import React, { useState, useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Send, 
  Paperclip, 
  Smile, 
  MoreVertical, 
  Search,
  Phone,
  Video,
  User,
  Check,
  CheckCheck
} from 'lucide-react';
import Button from '../ui/Button';
import { Card } from';
import Image from 'next/image';

interface ChatMessage {
  id: string;
  sender: {
    id: string;
    name: string;
    avatar?: string;
  };
  content: string;
  timestamp: Date;
  status: 'sent' | 'delivered' | 'read';
  attachments?: {
    type: 'image' | 'document' | 'link';
    url: string;
    name?: string;
  }[];
}

interface ChatParticipant {
  id: string;
  name: string;
  avatar?: string;
  status: 'online' | 'away' | 'offline';
  lastSeen?: Date;
}

interface ChatAttachment {
  type: 'image' | 'document' | 'link';
  url: string;
  name?: string;
}

interface EnterpriseChatProps {
  participants: ChatParticipant[];
  messages: ChatMessage[];
  currentUser: ChatParticipant;
  onSendMessage?: (message: string, attachments?: ChatAttachment[]) => void;
  onParticipantSelect?: (participant: ChatParticipant) => void;
  className?: string;
  title?: string;
}

const EnterpriseChat: React.FC<EnterpriseChatProps> = ({
  participants,
  messages,
  currentUser,
  onSendMessage,
  onParticipantSelect,
  className = '',
  title = 'Team Chat'
}) => {
  const [newMessage, setNewMessage] = useState('');
  const [selectedParticipant, setSelectedParticipant] = useState<ChatParticipant | null>(null);
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'online':
        return 'bg-green-500';
      case 'away':
        return 'bg-yellow-500';
      default:
        return 'bg-gray-500';
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'online':
        return 'Online';
      case 'away':
        return 'Away';
      default:
        return 'Offline';
    }
  };

  const getMessageStatusIcon = (status: string) => {
    switch (status) {
      case 'sent':
        return <Check className="w-4 h-4 text-gray-400" />;
      case 'delivered':
        return <CheckCheck className="w-4 h-4 text-gray-400" />;
      case 'read':
        return <CheckCheck className="w-4 h-4 text-blue-500" />;
      default:
        return null;
    }
  };

  const scrollToBottom = () => {
    if (messagesEndRef.current && messagesEndRef.current.scrollIntoView) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = () => {
    if (newMessage.trim() && onSendMessage) {
      onSendMessage(newMessage.trim());
      setNewMessage('');
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  return (
    <div className={`flex h-[600px] ${className}`}>
      {/* Participants Sidebar */}
      <div className="w-1/3 border-r border-gray-200 dark:border-gray-700 flex flex-col">
        <Card className="p-4 rounded-none rounded-tl-lg">
          <div className="flex items-center justify-between">
            <h2 className="text-lg font-semibold text-gray-900 dark:text-white">{title}</h2>
            <Button variant="ghost" size="sm" icon={<MoreVertical className="w-4 h-4" />}>
              &nbsp;
            </Button>
          </div>
          
          <div className="mt-3 relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
            <input
              type="text"
              placeholder="Search conversations..."
              className="w-full pl-10 pr-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-white focus:ring-accent focus:border-accent"
            />
          </div>
        </Card>
        
        <div className="flex-1 overflow-y-auto">
          {participants.map(participant => (
            <div
              key={participant.id}
              className={`p-4 border-b border-gray-100 dark:border-gray-800 cursor-pointer hover:bg-gray-50 dark:hover:bg-gray-800/50 transition-colors ${
                selectedParticipant?.id === participant.id ? 'bg-gray-100 dark:bg-gray-800' : ''
              }`}
              onClick={() => {
                setSelectedParticipant(participant);
                if (onParticipantSelect) {
                  onParticipantSelect(participant);
                }
              }}
            >
              <div className="flex items-center">
                {participant.avatar ? (
                  <div className="relative w-10 h-10 rounded-full">
                    <Image 
                      src={participant.avatar} 
                      alt={participant.name} 
                      fill
                      className="object-cover rounded-full"
                    />
                  </div>
                ) : (
                  <div className="w-10 h-10 rounded-full bg-gray-200 dark:bg-gray-700 flex items-center justify-center">
                    <User className="w-5 h-5 text-gray-500 dark:text-gray-400" />
                  </div>
                )}
                <div className="ml-3 flex-1">
                  <div className="flex items-center justify-between">
                    <h3 className="font-medium text-gray-900 dark:text-white">{participant.name}</h3>
                    <span className={`w-2 h-2 rounded-full ${getStatusColor(participant.status)}`}></span>
                  </div>
                  <p className="text-sm text-gray-500 dark:text-gray-400 mt-1">
                    {getStatusText(participant.status)}
                  </p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
      
      {/* Chat Area */}
      <div className="flex-1 flex flex-col">
        {/* Chat Header */}
        {selectedParticipant && (
          <Card className="p-4 rounded-none rounded-tr-lg border-b border-gray-200 dark:border-gray-700">
            <div className="flex items-center justify-between">
              <div className="flex items-center">
                {selectedParticipant.avatar ? (
                  <div className="relative w-10 h-10 rounded-full">
                    <Image 
                      src={selectedParticipant.avatar} 
                      alt={selectedParticipant.name} 
                      fill
                      className="object-cover rounded-full"
                    />
                  </div>
                ) : (
                  <div className="w-10 h-10 rounded-full bg-gray-200 dark:bg-gray-700 flex items-center justify-center">
                    <User className="w-5 h-5 text-gray-500 dark:text-gray-400" />
                  </div>
                )}
                <div className="ml-3">
                  <h3 className="font-medium text-gray-900 dark:text-white">{selectedParticipant.name}</h3>
                  <p className="text-sm text-gray-500 dark:text-gray-400">
                    {getStatusText(selectedParticipant.status)}
                  </p>
                </div>
              </div>
              
              <div className="flex space-x-2">
                <Button variant="ghost" size="sm" icon={<Phone className="w-4 h-4" />}>
                  &nbsp;
                </Button>
                <Button variant="ghost" size="sm" icon={<Video className="w-4 h-4" />}>
                  &nbsp;
                </Button>
                <Button variant="ghost" size="sm" icon={<MoreVertical className="w-4 h-4" />}>
                  &nbsp;
                </Button>
              </div>
            </div>
          </Card>
        )}
        
        {/* Messages */}
        <div className="flex-1 overflow-y-auto p-4 bg-gray-50 dark:bg-gray-900">
          <AnimatePresence>
            {messages.map((message) => (
              <motion.div
                key={message.id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                className={`flex mb-4 ${
                  message.sender.id === currentUser.id ? 'justify-end' : 'justify-start'
                }`}
              >
                {message.sender.id !== currentUser.id && (
                  <div className="mr-2">
                    {message.sender.avatar ? (
                      <div className="relative w-8 h-8 rounded-full">
                        <Image 
                          src={message.sender.avatar} 
                          alt={message.sender.name} 
                          fill
                          className="object-cover rounded-full"
                        />
                      </div>
                    ) : (
                      <div className="w-8 h-8 rounded-full bg-gray-200 dark:bg-gray-700 flex items-center justify-center">
                        <User className="w-4 h-4 text-gray-500 dark:text-gray-400" />
                      </div>
                    )}
                  </div>
                )}
                
                <div
                  className={`max-w-xs lg:max-w-md px-4 py-2 rounded-lg ${
                    message.sender.id === currentUser.id
                      ? 'bg-accent text-white rounded-br-none'
                      : 'bg-white dark:bg-gray-800 text-gray-900 dark:text-white rounded-bl-none border border-gray-200 dark:border-gray-700'
                  }`}
                >
                  {message.sender.id !== currentUser.id && (
                    <p className="text-xs font-medium mb-1 text-gray-600 dark:text-gray-400">
                      {message.sender.name}
                    </p>
                  )}
                  <p>{message.content}</p>
                  <div className={`flex items-center justify-end mt-1 ${
                    message.sender.id === currentUser.id ? 'text-accent-100' : 'text-gray-500 dark:text-gray-400'
                  }`}>
                    <span className="text-xs">
                      {message.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                    </span>
                    {message.sender.id === currentUser.id && (
                      <span className="ml-1">
                        {getMessageStatusIcon(message.status)}
                      </span>
                    )}
                  </div>
                </div>
                
                {message.sender.id === currentUser.id && (
                  <div className="ml-2">
                    {currentUser.avatar ? (
                      <div className="relative w-8 h-8 rounded-full">
                        <Image 
                          src={currentUser.avatar} 
                          alt={currentUser.name} 
                          fill
                          className="object-cover rounded-full"
                        />
                      </div>
                    ) : (
                      <div className="w-8 h-8 rounded-full bg-gray-200 dark:bg-gray-700 flex items-center justify-center">
                        <User className="w-4 h-4 text-gray-500 dark:text-gray-400" />
                      </div>
                    )}
                  </div>
                )}
              </motion.div>
            ))}
          </AnimatePresence>
          <div ref={messagesEndRef} />
        </div>
        
        {/* Message Input */}
        {selectedParticipant && (
          <Card className="p-4 rounded-none rounded-br-lg border-t border-gray-200 dark:border-gray-700">
            <div className="flex items-end">
              <div className="flex-1 relative">
                <textarea
                  value={newMessage}
                  onChange={(e) => setNewMessage(e.target.value)}
                  onKeyPress={handleKeyPress}
                  placeholder={`Message ${selectedParticipant.name}...`}
                  className="w-full px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-white focus:ring-accent focus:border-accent resize-none"
                  rows={1}
                />
                <Button
                  variant="ghost"
                  size="sm"
                  className="absolute right-2 bottom-2"
                  onClick={() => setShowEmojiPicker(!showEmojiPicker)}
                >
                  <Smile className="w-4 h-4" />
                </Button>
              </div>
              
              <div className="ml-2 flex space-x-2">
                <Button variant="ghost" size="sm" icon={<Paperclip className="w-4 h-4" />}>
                  &nbsp;
                </Button>
                <Button
                  variant="primary"
                  size="sm"
                  icon={<Send className="w-4 h-4" />}
                  onClick={handleSendMessage}
                  disabled={!newMessage.trim()}
                >
                  &nbsp;
                </Button>
              </div>
            </div>
            
            {/* Emoji Picker */}
            <AnimatePresence>
              {showEmojiPicker && (
                <motion.div
                  initial={{ opacity: 0, height: 0 }}
                  animate={{ opacity: 1, height: 'auto' }}
                  exit={{ opacity: 0, height: 0 }}
                  className="mt-2 p-2 bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-700 rounded-lg"
                >
                  <div className="grid grid-cols-8 gap-1">
                    {['😀', '😂', '😍', '😎', '👍', '👏', '🎉', '❤️'].map((emoji, index) => (
                      <button
                        key={index}
                        className="p-1 hover:bg-gray-100 dark:hover:bg-gray-700 rounded text-lg"
                        onClick={() => {
                          setNewMessage(prev => prev + emoji);
                          setShowEmojiPicker(false);
                        }}
                      >
                        {emoji}
                      </button>
                    ))}
                  </div>
                </motion.div>
              )}
            </AnimatePresence>
          </Card>
        )}
      </div>
    </div>
  );
};

export default EnterpriseChat;
export type { ChatMessage, ChatParticipant, ChatAttachment };

import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { 
  User, 
  Mail, 
  Phone, 
  Building, 
  MapPin, 
  Calendar,
  Save,
  X,
  AlertCircle,
  CheckCircle
} from 'lucide-react';
import Button from './Button';
import { Card } from';

interface FormField {
  id: string;
  label: string;
  type: string;
  required?: boolean;
  placeholder?: string;
  options?: { value: string; label: string }[];
  validation?: (value: string) => string | null;
}

interface EnterpriseFormProps {
  title: string;
  description?: string;
  fields: FormField[];
  onSubmit: (data: Record<string, string>) => void;
  onCancel?: () => void;
  submitLabel?: string;
  cancelLabel?: string;
  className?: string;
}

const EnterpriseForm: React.FC<EnterpriseFormProps> = ({
  title,
  description,
  fields,
  onSubmit,
  onCancel,
  submitLabel = 'Save',
  cancelLabel = 'Cancel',
  className = ''
}) => {
  const [formData, setFormData] = useState<Record<string, string>>(
    fields.reduce((acc, field) => ({ ...acc, [field.id]: '' }), {})
  );
  const [errors, setErrors] = useState<Record<string, string>>({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitSuccess, setSubmitSuccess] = useState(false);

  const validateField = (field: FormField, value: string): string | null => {
    if (field.required && !value.trim()) {
      return `${field.label} is required`;
    }
    
    if (field.validation) {
      return field.validation(value);
    }
    
    return null;
  };

  const handleChange = (id: string, value: string) => {
    setFormData(prev => ({ ...prev, [id]: value }));
    
    // Clear error when user starts typing
    if (errors[id]) {
      setErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors[id];
        return newErrors;
      });
    }
  };

  const handleBlur = (field: FormField) => {
    const error = validateField(field, formData[field.id]);
    if (error) {
      setErrors(prev => ({ ...prev, [field.id]: error }));
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    
    // Validate all fields
    const newErrors: Record<string, string> = {};
    fields.forEach(field => {
      const error = validateField(field, formData[field.id]);
      if (error) {
        newErrors[field.id] = error;
      }
    });
    
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }
    
    setIsSubmitting(true);
    try {
      await onSubmit(formData);
      setSubmitSuccess(true);
      // Reset success message after 3 seconds
      setTimeout(() => setSubmitSuccess(false), 3000);
    } catch (error) {
      console.error('Form submission error:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  const getInputIcon = (type: string) => {
    switch (type) {
      case 'text':
      case 'name':
        return <User className="w-5 h-5 text-gray-400" />;
      case 'email':
        return <Mail className="w-5 h-5 text-gray-400" />;
      case 'tel':
      case 'phone':
        return <Phone className="w-5 h-5 text-gray-400" />;
      case 'company':
        return <Building className="w-5 h-5 text-gray-400" />;
      case 'address':
        return <MapPin className="w-5 h-5 text-gray-400" />;
      case 'date':
        return <Calendar className="w-5 h-5 text-gray-400" />;
      default:
        return null;
    }
  };

  return (
    <Card className={`p-6 ${className}`} variant="feature">
      <div className="mb-6">
        <h2 className="text-xl font-bold text-gray-900 dark:text-white">{title}</h2>
        {description && (
          <p className="mt-1 text-sm text-gray-600 dark:text-gray-400">
            {description}
          </p>
        )}
      </div>

      {submitSuccess && (
        <motion.div
          initial={{ opacity: 0, y: -10 }}
          animate={{ opacity: 1, y: 0 }}
          className="mb-6 p-4 rounded-lg bg-green-50 dark:bg-green-900/20 border border-green-200 dark:border-green-800 flex items-center"
        >
          <CheckCircle className="w-5 h-5 text-green-500 mr-2" />
          <span className="text-sm text-green-700 dark:text-green-300">
            Form submitted successfully!
          </span>
        </motion.div>
      )}

      <form onSubmit={handleSubmit} className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {fields.map((field) => (
            <div key={field.id} className="space-y-1">
              <label 
                htmlFor={field.id} 
                className="block text-sm font-medium text-gray-700 dark:text-gray-300"
              >
                {field.label} {field.required && <span className="text-red-500">*</span>}
              </label>
              
              {field.type === 'select' ? (
                <div className="relative">
                  <select
                    id={field.id}
                    value={formData[field.id]}
                    onChange={(e) => handleChange(field.id, e.target.value)}
                    onBlur={() => handleBlur(field)}
                    className={`w-full pl-10 pr-3 py-2 border ${
                      errors[field.id] 
                        ? 'border-red-300 focus:ring-red-500 focus:border-red-500' 
                        : 'border-gray-300 dark:border-gray-600 focus:ring-accent focus:border-accent'
                    } rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-white shadow-sm transition-colors`}
                  >
                    <option value="">{field.placeholder || 'Select an option'}</option>
                    {field.options?.map((option) => (
                      <option key={option.value} value={option.value}>
                        {option.label}
                      </option>
                    ))}
                  </select>
                  <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                    {getInputIcon('text')}
                  </div>
                </div>
              ) : field.type === 'textarea' ? (
                <textarea
                  id={field.id}
                  value={formData[field.id]}
                  onChange={(e) => handleChange(field.id, e.target.value)}
                  onBlur={() => handleBlur(field)}
                  placeholder={field.placeholder}
                  rows={4}
                  className={`w-full px-3 py-2 border ${
                    errors[field.id] 
                      ? 'border-red-300 focus:ring-red-500 focus:border-red-500' 
                      : 'border-gray-300 dark:border-gray-600 focus:ring-accent focus:border-accent'
                  } rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-white shadow-sm transition-colors`}
                />
              ) : (
                <div className="relative">
                  <input
                    type={field.type}
                    id={field.id}
                    value={formData[field.id]}
                    onChange={(e) => handleChange(field.id, e.target.value)}
                    onBlur={() => handleBlur(field)}
                    placeholder={field.placeholder}
                    className={`w-full pl-10 pr-3 py-2 border ${
                      errors[field.id] 
                        ? 'border-red-300 focus:ring-red-500 focus:border-red-500' 
                        : 'border-gray-300 dark:border-gray-600 focus:ring-accent focus:border-accent'
                    } rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-white shadow-sm transition-colors`}
                  />
                  <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                    {getInputIcon(field.type)}
                  </div>
                </div>
              )}
              
              {errors[field.id] && (
                <motion.div
                  initial={{ opacity: 0, height: 0 }}
                  animate={{ opacity: 1, height: 'auto' }}
                  className="flex items-center mt-1"
                >
                  <AlertCircle className="w-4 h-4 text-red-500 mr-1" />
                  <p className="text-sm text-red-600 dark:text-red-400">
                    {errors[field.id]}
                  </p>
                </motion.div>
              )}
            </div>
          ))}
        </div>

        <div className="flex justify-end space-x-3 pt-4">
          {onCancel && (
            <Button
              type="button"
              variant="outline"
              onClick={onCancel}
              disabled={isSubmitting}
              icon={<X className="w-4 h-4" />}
            >
              {cancelLabel}
            </Button>
          )}
          <Button
            type="submit"
            variant="primary"
            disabled={isSubmitting}
            icon={isSubmitting ? undefined : <Save className="w-4 h-4" />}
          >
            {isSubmitting ? (
              <span className="flex items-center">
                <span className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></span>
                Saving...
              </span>
            ) : (
              submitLabel
            )}
          </Button>
        </div>
      </form>
    </Card>
  );
};

export default EnterpriseForm;

import React, { useState } from 'react';
import {
  LayoutDashboard,
  FolderKanban,
  Users,
  UserCheck,
  DollarSign,
  Calendar,
  CheckSquare,
  Bot,
  Shield,
  TrendingUp,
  FileText,
  BarChart3,
  Settings,
  HelpCircle,
  LogOut,
  ChevronDown,
  ChevronRight,
  Menu,
  X,
  Bell,
  Search,
  Zap
} from 'lucide-react';
import Button from '../ui/Button';
import Image from 'next/image';
import AIChatWidget from './AIChatWidget';

interface NavItem {
  id: string;
  title: string;
  icon: React.ReactNode;
  href: string;
  children?: NavItem[];
  badge?: string;
}

interface EnterpriseNavigationProps {
  user?: {
    name: string;
    email: string;
    avatar?: string;
  };
  onLogout?: () => void;
  onToggleAIChat?: () => void;
  className?: string;
}

const EnterpriseNavigation: React.FC<EnterpriseNavigationProps> = ({
  user,
  onLogout,
  onToggleAIChat,
  className = ''
}) => {
  const [expandedItems, setExpandedItems] = useState<Record<string, boolean>>({});
