        {
          id: 'client-list',
          title: 'Client Directory',
          icon: <Users className="w-4 h-4" />,
          href: '/enterprise/clients',
        },
        {
          id: 'client-profiles',
          title: 'Client Profiles',
          icon: <UserCheck className="w-4 h-4" />,
