    // 6. Calendar, Tasks, and Meetings
    {
      id: 'calendar',
      title: 'Calendar & Tasks',
      icon: <Calendar className="w-5 h-5" />,
      href: '/enterprise/calendar',
      children: [
        {
          id: 'calendar-view',
          title: 'Calendar View',
          icon: <Calendar className="w-4 h-4" />,
          href: '/enterprise/calendar',
