          id: 'qa-checklists',
          title: 'Quality Checklists',
          icon: <CheckSquare className="w-4 h-4" />,
          href: '/enterprise/qa/checklists',
        },
        {
          id: 'qa-reports',
          title: 'QA Reports',
          icon: <FileText className="w-4 h-4" />,
          href: '/enterprise/qa/reports',
        },
      ],
    },

    // 9. Leads & Sales
    {
      id: 'leads',
      title: 'Leads & Sales',
      icon: <TrendingUp className="w-5 h-5" />,
      href: '/enterprise/leads',
      children: [
        {
          id: 'leads-crm',
          title: 'CRM Dashboard',
          icon: <TrendingUp className="w-4 h-4" />,
          href: '/enterprise/leads',
        },
        {
          id: 'leads-pipeline',
          title: 'Sales Pipeline',
          icon: <BarChart3 className="w-4 h-4" />,
          href: '/enterprise/leads/pipeline',
        },
        {
          id: 'leads-conversion',
          title: 'Conversion Tracking',
          icon: <TrendingUp className="w-4 h-4" />,
          href: '/enterprise/leads/conversion',
        },
      ],
    },

    // Settings & Configuration
    {
      id: 'settings',
      title: 'Settings & Admin',
      icon: <Settings className="w-5 h-5" />,
      href: '/enterprise/settings',
    },
  ];

  const toggleExpand = (id: string) => {
    setExpandedItems(prev => ({
      ...prev,
      [id]: !prev[id]
    }));
  };

  const NavItemComponent: React.FC<{ item: NavItem; depth?: number }> = ({ item, depth = 0 }) => {
    const hasChildren = item.children && item.children.length > 0;
    const isExpanded = expandedItems[item.id];
    
    return (
      <div>
        <a
          href={item.href}
          className={`flex items-center justify-between px-4 py-3 text-sm font-medium transition-colors ${
            depth === 0 
              ? 'text-gray-700 hover:bg-gray-100 dark:text-gray-300 dark:hover:bg-gray-800' 
              : 'text-gray-600 hover:bg-gray-100 dark:text-gray-400 dark:hover:bg-gray-800 pl-8'
          }`}
          onClick={(e) => {
            if (hasChildren) {
              e.preventDefault();
              toggleExpand(item.id);
            }
          }}
        >
          <div className="flex items-center">
            <span className={`${depth > 0 ? 'ml-2' : ''}`}>
              {item.icon}
            </span>
            <span className="ml-3">{item.title}</span>
            {item.badge && (
              <span className="ml-2 px-2 py-0.5 text-xs rounded-full bg-accent text-white">
                {item.badge}
              </span>
            )}
          </div>
          
          {hasChildren && (
            <span>
              {isExpanded ? (
                <ChevronDown className="w-4 h-4" />
              ) : (
                <ChevronRight className="w-4 h-4" />
              )}
            </span>
          )}
        </a>
        
        {hasChildren && isExpanded && (
          <div className="mt-1">
            {item.children?.map(child => (
              <NavItemComponent key={child.id} item={child} depth={depth + 1} />
            ))}
          </div>
        )}
      </div>
    );
  };

  return (
    <>
      {/* Mobile menu button */}
      <div className="md:hidden p-4 border-b border-gray-200 dark:border-gray-700">
        <Button
          variant="ghost"
          size="sm"
          icon={mobileMenuOpen ? <X className="w-5 h-5" /> : <Menu className="w-5 h-5" />}
          onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
        >
          &nbsp;
        </Button>
      </div>

      {/* Sidebar */}
      <div 
        className={`fixed inset-y-0 left-0 z-40 w-64 bg-white dark:bg-gray-900 border-r border-gray-200 dark:border-gray-800 transform transition-transform duration-300 ease-in-out md:translate-x-0 ${
          mobileMenuOpen ? 'translate-x-0' : '-translate-x-full'
        } ${className}`}
      >
        <div className="flex flex-col h-full">
          {/* Logo */}
          <div className="flex items-center justify-between px-6 py-4 border-b border-gray-200 dark:border-gray-800">
            <div className="flex items-center">
              <div className="w-8 h-8 rounded-lg bg-accent flex items-center justify-center">
                <span className="text-white font-bold text-lg">E</span>
              </div>
              <span className="ml-3 text-xl font-bold text-gray-900 dark:text-white">Enterprise</span>
            </div>
          </div>

          {/* Navigation */}
          <nav className="flex-1 overflow-y-auto py-4">
            {navItems.map(item => (
              <NavItemComponent key={item.id} item={item} />
            ))}
          </nav>

          {/* User profile and footer */}
          <div className="border-t border-gray-200 dark:border-gray-800 p-4">
            {user && (
              <div className="flex items-center px-2 py-3">
                {user.avatar ? (
                  <div className="relative w-10 h-10 rounded-full">
                    <Image 
                      src={user.avatar} 
                      alt={user.name} 
                      fill
                      className="object-cover rounded-full"
                    />
                  </div>
                ) : (
                  <div className="w-10 h-10 rounded-full bg-gray-200 dark:bg-gray-700 flex items-center justify-center">
                    <span className="text-gray-700 dark:text-gray-300 font-medium">
                      {user.name.charAt(0)}
                    </span>
                  </div>
                )}
                <div className="ml-3">
                  <p className="text-sm font-medium text-gray-900 dark:text-white">{user.name}</p>
                  <p className="text-xs text-gray-500 dark:text-gray-400">{user.email}</p>
                </div>
              </div>
            )}
            
            <div className="mt-4 space-y-1">
              <button
                onClick={onToggleAIChat}
                className="w-full flex items-center px-4 py-2 text-sm font-medium text-gray-700 hover:bg-gray-100 dark:text-gray-300 dark:hover:bg-gray-800 rounded-lg transition-colors"
              >
                <Bot className="w-5 h-5" />
                <span className="ml-3">AI Assistant</span>
                <span className="ml-auto px-2 py-0.5 text-xs rounded-full bg-accent text-white">
                  Auralis
                </span>
              </button>

              <a
                href="/help"
                className="flex items-center px-4 py-2 text-sm font-medium text-gray-700 hover:bg-gray-100 dark:text-gray-300 dark:hover:bg-gray-800 rounded-lg transition-colors"
              >
                <HelpCircle className="w-5 h-5" />
                <span className="ml-3">Help Center</span>
              </a>

              {onLogout && (
                <button
                  onClick={onLogout}
                  className="w-full flex items-center px-4 py-2 text-sm font-medium text-gray-700 hover:bg-gray-100 dark:text-gray-300 dark:hover:bg-gray-800 rounded-lg transition-colors"
                >
                  <LogOut className="w-5 h-5" />
                  <span className="ml-3">Sign out</span>
                </button>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Overlay for mobile */}
      {mobileMenuOpen && (
        <div 
          className="fixed inset-0 z-30 bg-black bg-opacity-50 md:hidden"
          onClick={() => setMobileMenuOpen(false)}
        />
      )}
    </>
  );
};

export default EnterpriseNavigation;

import React, { useState } from 'react';
import {
  useReactTable,
  getCoreRowModel,
  getSortedRowModel,
  getPaginationRowModel,
  getFilteredRowModel,
  ColumnDef,
  SortingState,
  ColumnFiltersState,
  VisibilityState,
  flexRender,
} from '@tanstack/react-table';
import { 
  ChevronUp, 
  ChevronDown, 
  Loader2,
  Search,
  Filter,
  Download,
  Eye,
  Edit,
  Trash2,
  MoreHorizontal,
  Plus,
  RefreshCw
} from 'lucide-react';
import Button from './Button';
import { Card } from';

interface EnterpriseTableProps<T> {
  data: T[];
  columns: ColumnDef<T, unknown>[];
  onRowClick?: (row: T) => void;
  onEdit?: (row: T) => void;
  onDelete?: (row: T) => void;
  onView?: (row: T) => void;
  onAddNew?: () => void;
  onRefresh?: () => void;
  searchable?: boolean;
  filterable?: boolean;
  exportable?: boolean;
  pagination?: boolean;
  itemsPerPage?: number;
  className?: string;
  title?: string;
  description?: string;
  loading?: boolean;
  onSearch?: (searchTerm: string) => void;
  searchPlaceholder?: string;
}

const EnterpriseTable = <T extends Record<string, unknown>>({
  data,
  columns,
  onRowClick,
  onEdit,
  onDelete,
  onView,
  onAddNew,
  onRefresh,
  searchable = true,
  filterable = true,
  exportable = true,
  pagination = true,
  itemsPerPage = 10,
  className = '',
  title,
  description,
  loading = false,
  onSearch,
  searchPlaceholder = 'Search...'
}: EnterpriseTableProps<T>) => {
  const [sorting, setSorting] = useState<SortingState>([]);
  const [columnVisibility, setColumnVisibility] = useState<VisibilityState>({});
  const [columnFilters, setColumnFilters] = useState<ColumnFiltersState>([]);
  const [globalFilter, setGlobalFilter] = useState('');
  const [pageIndex, setPageIndex] = useState(0);
  
  // Add action column if any action handlers are provided
  const hasActions = !!(onEdit || onDelete || onView);
  
  const actionColumn: ColumnDef<T> = {
    id: 'actions',
    header: 'Actions',
    cell: ({ row }) => (
      <div className="flex items-center space-x-2">
        {onView && (
          <Button 
            variant="ghost" 
            size="sm" 
            icon={<Eye className="w-4 h-4" />}
            onClick={(e) => {
              e.stopPropagation();
              onView(row.original);
            }}
          >
            &nbsp;
          </Button>
        )}
        {onEdit && (
          <Button 
            variant="ghost" 
            size="sm" 
            icon={<Edit className="w-4 h-4" />}
            onClick={(e) => {
              e.stopPropagation();
              onEdit(row.original);
            }}
          >
            &nbsp;
          </Button>
        )}
        {onDelete && (
          <Button 
            variant="ghost" 
            size="sm" 
            icon={<Trash2 className="w-4 h-4" />}
            onClick={(e) => {
              e.stopPropagation();
              onDelete(row.original);
            }}
          >
            &nbsp;
          </Button>
        )}
        {!onView && !onEdit && !onDelete && (
          <Button 
            variant="ghost" 
            size="sm" 
            icon={<MoreHorizontal className="w-4 h-4" />}
            onClick={(e) => {
              e.stopPropagation();
              // Handle menu open if needed
            }}
          >
            &nbsp;
          </Button>
        )}
      </div>
    ),
  };
  
  // Add action column to columns if actions are available
  const enhancedColumns = hasActions ? [...columns, actionColumn] : columns;

  const table = useReactTable({
    data,
    columns: enhancedColumns,
    state: {
      sorting,
      columnVisibility,
      columnFilters,
      globalFilter,
      pagination: {
        pageIndex,
        pageSize: itemsPerPage,
      },
    },
    onSortingChange: setSorting,
    onColumnVisibilityChange: setColumnVisibility,
    onColumnFiltersChange: setColumnFilters,
    onGlobalFilterChange: setGlobalFilter,
    getCoreRowModel: getCoreRowModel(),
    getSortedRowModel: getSortedRowModel(),
    getFilteredRowModel: getFilteredRowModel(),
    getPaginationRowModel: pagination ? getPaginationRowModel() : undefined,
    manualPagination: false,
    pageCount: pagination ? Math.ceil(data.length / itemsPerPage) : -1,
    onPaginationChange: (updater) => {
      if (typeof updater === 'function') {
        const newState = updater({ pageIndex, pageSize: itemsPerPage });
        setPageIndex(newState.pageIndex);
      }
    },
  });

  const handleSearch = (value: string) => {
    setGlobalFilter(value);
    if (onSearch) {
      onSearch(value);
    }
  };

  return (
    <Card className={`p-6 ${className}`} variant="feature">
      {/* Header */}
      {(title || description || onAddNew || onRefresh || searchable || exportable) && (
        <div className="mb-6">
          <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-4">
            <div>
              {title && (
                <h2 className="text-xl font-bold text-gray-900 dark:text-white">{title}</h2>
              )}
              {description && (
                <p className="text-sm text-gray-600 dark:text-gray-400 mt-1">{description}</p>
              )}
            </div>
            
            <div className="flex flex-wrap items-center gap-2">
              {searchable && (
                <div className="relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
                  <input
                    type="text"
                    placeholder={searchPlaceholder}
                    value={globalFilter}
                    onChange={(e) => handleSearch(e.target.value)}
                    className="pl-10 pr-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-white focus:ring-accent focus:border-accent"
                  />
                </div>
              )}
              
              {filterable && (
                <Button variant="outline" size="sm" icon={<Filter className="w-4 h-4" />}>
                  Filter
                </Button>
              )}
              
              {exportable && (
                <Button variant="outline" size="sm" icon={<Download className="w-4 h-4" />}>
                  Export
                </Button>
              )}
              
              {onRefresh && (
                <Button 
                  variant="outline" 
                  size="sm" 
                  icon={<RefreshCw className="w-4 h-4" />}
                  onClick={onRefresh}
                >
                  &nbsp;
                </Button>
              )}
              
              {onAddNew && (
                <Button 
                  variant="primary" 
                  size="sm" 
                  icon={<Plus className="w-4 h-4" />}
                  onClick={onAddNew}
                >
                  Add New
                </Button>
              )}
            </div>
          </div>
          
          {/* Column Visibility Controls */}
          <div className="flex flex-wrap gap-2 mt-4 p-3 bg-gray-50 dark:bg-gray-800 rounded-lg">
            <span className="text-sm font-medium text-gray-700 dark:text-gray-300 mr-2">
              Columns:
            </span>
            {table.getAllColumns().filter(column => column.id !== 'actions').map((column) => (
              <label key={column.id} className="flex items-center text-sm text-gray-700 dark:text-gray-300">
                <input
                  type="checkbox"
                  checked={column.getIsVisible()}
                  onChange={column.getToggleVisibilityHandler()}
                  className="mr-2 rounded text-accent focus:ring-accent"
                />
                {column.id}
              </label>
            ))}
          </div>
        </div>
      )}

      {/* Table */}
      <div className="overflow-x-auto border border-gray-200 dark:border-gray-700 rounded-lg">
        <table className="min-w-full divide-y divide-gray-200 dark:divide-gray-700">
          <thead className="bg-gray-50 dark:bg-gray-800">
            {table.getHeaderGroups().map((headerGroup) => (
              <tr key={headerGroup.id}>
                {headerGroup.headers.map((header) => (
                  <th
                    key={header.id}
                    className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-400 uppercase tracking-wider"
                  >
                    {header.isPlaceholder ? null : (
                      <div
                        className={`flex items-center space-x-1 cursor-pointer select-none ${
                          header.column.getCanSort() ? 'hover:text-gray-700 dark:hover:text-gray-300' : ''
                        }`}
                        onClick={header.column.getToggleSortingHandler()}
                      >
                        <span>{flexRender(header.column.columnDef.header, header.getContext())}</span>
                        {header.column.getCanSort() && (
                          <div className="flex flex-col">
                            <ChevronUp
                              className={`w-3 h-3 ${
                                header.column.getIsSorted() === 'asc'
                                  ? 'text-blue-600'
                                  : 'text-gray-400'
                              }`}
                            />
                            <ChevronDown
                              className={`w-3 h-3 -mt-1 ${
                                header.column.getIsSorted() === 'desc'
                                  ? 'text-blue-600'
                                  : 'text-gray-400'
                              }`}
                            />
                          </div>
                        )}
                      </div>
                    )}
                  </th>
                ))}
              </tr>
            ))}
          </thead>
          <tbody className="bg-white dark:bg-gray-900 divide-y divide-gray-200 dark:divide-gray-700">
            {loading ? (
              <tr>
                <td colSpan={enhancedColumns.length} className="px-6 py-4 text-center">
                  <div className="flex items-center justify-center">
                    <Loader2 className="w-6 h-6 animate-spin text-blue-600" />
                    <span className="ml-2 text-gray-500 dark:text-gray-400">Loading...</span>
                  </div>
                </td>
              </tr>
            ) : table.getRowModel().rows.length === 0 ? (
              <tr>
                <td colSpan={enhancedColumns.length} className="px-6 py-4 text-center text-gray-500 dark:text-gray-400">
                  No data available
                </td>
              </tr>
            ) : (
              table.getRowModel().rows.map((row) => (
                <tr 
                  key={row.id} 
                  className={`hover:bg-gray-50 dark:hover:bg-gray-800 ${onRowClick ? 'cursor-pointer' : ''}`}
                  onClick={() => onRowClick && onRowClick(row.original)}
                >
                  {row.getVisibleCells().map((cell) => (
                    <td key={cell.id} className="px-6 py-4 whitespace-nowrap text-sm text-gray-900 dark:text-gray-100">
                      {flexRender(cell.column.columnDef.cell, cell.getContext())}
                    </td>
                  ))}
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      {/* Pagination */}
      {pagination && (
        <div className="flex items-center justify-between px-4 py-3 bg-white dark:bg-gray-800 border-t border-gray-200 dark:border-gray-700 rounded-b-lg mt-4">
          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-700 dark:text-gray-300">
              Showing {table.getState().pagination.pageIndex * table.getState().pagination.pageSize + 1} to{' '}
              {Math.min((table.getState().pagination.pageIndex + 1) * table.getState().pagination.pageSize, data.length)} of {data.length} results
            </span>
          </div>

          <div className="flex items-center space-x-2">
            <Button
              variant="outline"
              size="sm"
              onClick={() => table.setPageIndex(0)}
              disabled={!table.getCanPreviousPage()}
            >
              First
            </Button>
            <Button
              variant="outline"
              size="sm"
              onClick={() => table.previousPage()}
              disabled={!table.getCanPreviousPage()}
            >
              Previous
            </Button>
            <span className="text-sm text-gray-700 dark:text-gray-300">
              Page {table.getState().pagination.pageIndex + 1} of {table.getPageCount()}
            </span>
            <Button
              variant="outline"
              size="sm"
              onClick={() => table.nextPage()}
              disabled={!table.getCanNextPage()}
            >
              Next
            </Button>
            <Button
              variant="outline"
              size="sm"
              onClick={() => table.setPageIndex(table.getPageCount() - 1)}
              disabled={!table.getCanNextPage()}
            >
              Last
            </Button>
          </div>
        </div>
      )}
    </Card>
  );
};

export default EnterpriseTable;

'use client';

import React, { useState, useRef, useEffect } from 'react';
import { Search, X, FileText, Users, FolderKanban, Calendar, DollarSign, CheckSquare } from 'lucide-react';
import { createClientComponentClient } from '@supabase/auth-helpers-nextjs';

interface SearchResult {
  id: string;
  type: 'project' | 'task' | 'client' | 'document' | 'invoice' | 'meeting';
  title: string;
  subtitle?: string;
  url: string;
  icon: React.ReactNode;
  category: string;
}

interface GlobalSearchProps {
  isOpen: boolean;
  onClose: () => void;
  placeholder?: string;
}

const GlobalSearch: React.FC<GlobalSearchProps> = ({
  isOpen,
  onClose,
  placeholder = "Search projects, tasks, clients, documents..."
}) => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState<SearchResult[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [recentSearches, setRecentSearches] = useState<string[]>([]);
  const inputRef = useRef<HTMLInputElement>(null);
  const supabase = createClientComponentClient();

  useEffect(() => {
    if (isOpen && inputRef.current) {
      inputRef.current.focus();
    }
  }, [isOpen]);

  useEffect(() => {
    const search = async () => {
      if (!query.trim()) {
        setResults([]);
        return;
      }

      setIsLoading(true);
      try {
        const searchResults: SearchResult[] = [];

        // Search projects
        const { data: projects } = await supabase
          .from('projects')
          .select('id, name, description, status')
          .ilike('name', `%${query}%`)
          .limit(5);

        if (projects) {
          projects.forEach(project => {
            searchResults.push({
              id: project.id,
              type: 'project',
              title: project.name,
              subtitle: project.description,
              url: `/enterprise/projects/${project.id}`,
              icon: <FolderKanban className="w-4 h-4" />,
              category: 'Projects'
            });
          });
        }

        // Search tasks
        const { data: tasks } = await supabase
          .from('tasks')
          .select('id, title, description, status')
          .ilike('title', `%${query}%`)
          .limit(5);

        if (tasks) {
          tasks.forEach(task => {
            searchResults.push({
              id: task.id,
              type: 'task',
              title: task.title,
              subtitle: task.description,
              url: `/enterprise/tasks/${task.id}`,
              icon: <CheckSquare className="w-4 h-4" />,
              category: 'Tasks'
            });
          });
        }

        // Search clients
        const { data: clients } = await supabase
          .from('clients')
          .select('id, name, email, company')
          .or(`name.ilike.%${query}%,email.ilike.%${query}%,company.ilike.%${query}%`)
          .limit(5);

        if (clients) {
          clients.forEach(client => {
            searchResults.push({
              id: client.id,
              type: 'client',
              title: client.name,
              subtitle: client.company || client.email,
              url: `/enterprise/clients/${client.id}`,
              icon: <Users className="w-4 h-4" />,
              category: 'Clients'
            });
          });
        }

        // Search documents
        const { data: documents } = await supabase
          .from('documents')
          .select('id, title, document_type, project_id')
          .ilike('title', `%${query}%`)
          .limit(5);

        if (documents) {
          documents.forEach(doc => {
            searchResults.push({
              id: doc.id,
              type: 'document',
              title: doc.title,
              subtitle: doc.document_type,
              url: `/enterprise/documents/${doc.id}`,
              icon: <FileText className="w-4 h-4" />,
              category: 'Documents'
            });
          });
        }

        // Search invoices
        const { data: invoices } = await supabase
          .from('invoices')
          .select('id, invoice_number, client_id, total_amount')
          .ilike('invoice_number', `%${query}%`)
          .limit(5);

        if (invoices) {
          invoices.forEach(invoice => {
            searchResults.push({
              id: invoice.id,
              type: 'invoice',
              title: `Invoice ${invoice.invoice_number}`,
              subtitle: `$${invoice.total_amount}`,
              url: `/enterprise/billing/invoices/${invoice.id}`,
              icon: <DollarSign className="w-4 h-4" />,
              category: 'Billing'
            });
          });
        }

        setResults(searchResults);
      } catch (error) {
        console.error('Search error:', error);
        setResults([]);
      } finally {
        setIsLoading(false);
      }
    };

    const debounceTimer = setTimeout(search, 300);
    return () => clearTimeout(debounceTimer);
  }, [query, supabase]);

  const handleResultClick = (result: SearchResult) => {
    // Add to recent searches
    const updated = [query, ...recentSearches.filter(s => s !== query)].slice(0, 5);
    setRecentSearches(updated);
    localStorage.setItem('enterprise_recent_searches', JSON.stringify(updated));

    // Navigate to result
    window.location.href = result.url;
    onClose();
  };

  const clearSearch = () => {
    setQuery('');
    setResults([]);
  };

  // Load recent searches on mount
  useEffect(() => {
    const saved = localStorage.getItem('enterprise_recent_searches');
    if (saved) {
      setRecentSearches(JSON.parse(saved));
    }
  }, []);

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 z-50 bg-black bg-opacity-50 flex items-start justify-center pt-16">
      <div className="bg-white dark:bg-gray-800 rounded-lg shadow-2xl w-full max-w-2xl mx-4 max-h-[80vh] overflow-hidden">
        {/* Search Input */}
        <div className="p-4 border-b border-gray-200 dark:border-gray-700">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
            <input
              ref={inputRef}
              type="text"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              placeholder={placeholder}
              className="w-full pl-10 pr-10 py-3 text-lg border-0 focus:outline-none focus:ring-0 bg-transparent text-gray-900 dark:text-white placeholder-gray-500"
            />
            {query && (
              <button
                onClick={clearSearch}
                className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600"
              >
                <X className="w-5 h-5" />
              </button>
            )}
          </div>
        </div>

        {/* Results */}
        <div className="max-h-96 overflow-y-auto">
          {isLoading ? (
            <div className="p-8 text-center">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-accent mx-auto"></div>
              <p className="mt-2 text-gray-500 dark:text-gray-400">Searching...</p>
            </div>
          ) : results.length > 0 ? (
            <div className="divide-y divide-gray-200 dark:divide-gray-700">
              {results.map((result) => (
                <button
                  key={result.id}
                  onClick={() => handleResultClick(result)}
                  className="w-full px-4 py-3 text-left hover:bg-gray-50 dark:hover:bg-gray-700 transition-colors"
                >
                  <div className="flex items-center">
                    <div className="flex-shrink-0 mr-3">
                      {result.icon}
                    </div>
                    <div className="flex-1 min-w-0">
                      <p className="text-sm font-medium text-gray-900 dark:text-white truncate">
                        {result.title}
                      </p>
                      {result.subtitle && (
                        <p className="text-sm text-gray-500 dark:text-gray-400 truncate">
                          {result.subtitle}
                        </p>
                      )}
                    </div>
                    <div className="flex-shrink-0 ml-3">
                      <span className="inline-flex items-center px-2 py-0.5 rounded text-xs font-medium bg-gray-100 dark:bg-gray-700 text-gray-800 dark:text-gray-300">
                        {result.category}
                      </span>
                    </div>
                  </div>
                </button>
              ))}
            </div>
          ) : query ? (
            <div className="p-8 text-center">
              <Search className="mx-auto h-12 w-12 text-gray-400" />
              <h3 className="mt-2 text-sm font-medium text-gray-900 dark:text-white">No results found</h3>
              <p className="mt-1 text-sm text-gray-500 dark:text-gray-400">
                Try adjusting your search terms or check your spelling.
              </p>
            </div>
          ) : recentSearches.length > 0 ? (
            <div className="p-4">
              <h3 className="text-sm font-medium text-gray-900 dark:text-white mb-3">Recent Searches</h3>
              <div className="space-y-2">
                {recentSearches.map((search, index) => (
                  <button
                    key={index}
                    onClick={() => setQuery(search)}
                    className="w-full text-left px-3 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700 rounded-md transition-colors"
                  >
                    <Search className="inline w-4 h-4 mr-2" />
                    {search}
                  </button>
                ))}
              </div>
            </div>
          ) : (
            <div className="p-8 text-center">
              <Search className="mx-auto h-12 w-12 text-gray-400" />
              <h3 className="mt-2 text-sm font-medium text-gray-900 dark:text-white">Start typing to search</h3>
              <p className="mt-1 text-sm text-gray-500 dark:text-gray-400">
                Search across projects, tasks, clients, documents, and more.
              </p>
            </div>
          )}
        </div>

        {/* Footer */}
        <div className="px-4 py-3 bg-gray-50 dark:bg-gray-700 border-t border-gray-200 dark:border-gray-600">
          <div className="flex items-center justify-between text-xs text-gray-500 dark:text-gray-400">
            <div className="flex items-center space-x-4">
              <span>↑↓ to navigate</span>
              <span>↵ to select</span>
              <span>esc to close</span>
            </div>
            <button
              onClick={onClose}
              className="hover:text-gray-700 dark:hover:text-gray-300"
            >
              Close
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

